import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import BotonesModoOscuroVolver from '../components/BotonesModoOscuroVolver';
import { verificarCorreo } from '../utils/verificarCorreo';
import '../styles/light.css';
import '../styles/dark.css';

function FormularioDiscapacitados() {
  const [formData, setFormData] = useState({
    nombre: '',
    apellido: '',
    correo: '',
    password: '',
    confirmPassword: '',
    discapacidad: '',
    descripcion: ''
  });

  const [datoTemporal, setDatoTemporal] = useState('');
  const [modoOscuro, setModoOscuro] = useState(false);
  const [correoExistente, setCorreoExistente] = useState(false);
  const [validandoCorreo, setValidandoCorreo] = useState(false);
  const [pasoActual, setPasoActual] = useState('inicio');
  const [reconocimientoActivo, setReconocimientoActivo] = useState(false);
  const [mensajeEnviado, setMensajeEnviado] = useState(false);
  const [repetirTimer, setRepetirTimer] = useState(null);
  const [cargando, setCargando] = useState(false);
  const navigate = useNavigate();
  const recognitionRef = useRef(null);

  const nombreRef = useRef(null);
  const apellidoRef = useRef(null);
  const correoRef = useRef(null);
  const passwordRef = useRef(null);
  const confirmPasswordRef = useRef(null);
  const discapacidadRef = useRef(null);
  const descripcionRef = useRef(null);

  useEffect(() => {
    document.body.classList.toggle('dark', modoOscuro);

    if ('webkitSpeechRecognition' in window) {
      const recognition = new window.webkitSpeechRecognition();
      recognition.lang = 'es-ES';
      recognition.continuous = false;
      recognition.interimResults = false;

      recognition.onresult = (event) => {
        if (!event.results || !event.results[0]) return;
        const resultado = event.results[0][0].transcript.trim().toLowerCase();
        console.log("Texto reconocido:", resultado);
        manejarResultadoVoz(resultado);
      };

      recognition.onerror = (event) => {
        console.error('Error en reconocimiento:', event.error);
        hablar('Hubo un error con el micr칩fono. Por favor intenta nuevamente.');
      };

      recognitionRef.current = recognition;
    }

    iniciarPaso('inicio');

    return () => {
      if (repetirTimer) clearTimeout(repetirTimer);
      if (recognitionRef.current) recognitionRef.current.abort();
    };
  }, [modoOscuro]);

  useEffect(() => {
    const timer = setTimeout(() => {
      if (formData.correo) verificarCorreo(formData.correo, setCorreoExistente, setValidandoCorreo);
    }, 500);
    return () => clearTimeout(timer);
  }, [formData.correo]);

  const hablar = (texto, callback) => {
    if (cargando) return;

    setReconocimientoActivo(false);
    if (recognitionRef.current) recognitionRef.current.abort();
    if (repetirTimer) clearTimeout(repetirTimer);

    setCargando(true);
    const mensaje = new SpeechSynthesisUtterance(texto);
    mensaje.lang = 'es-ES';

    mensaje.onend = () => {
      setCargando(false);
      if (callback) callback();

      setTimeout(() => {
        setReconocimientoActivo(true);
        iniciarReconocimiento();
      }, 500);

      setRepetirTimer(setTimeout(() => {
        if (reconocimientoActivo) {
          hablar(texto, callback);
        }
      }, 10000));
    };

    window.speechSynthesis.speak(mensaje);
  };

  const iniciarPaso = (paso) => {
    setPasoActual(paso);
    switch (paso) {
      case 'inicio':
        hablar('Este es el formulario de registro para discapacitados visuales. Por favor, di tu nombre.');
        break;
      case 'confirmarNombre':
        hablar(`Dijiste que tu nombre es: ${datoTemporal}. 쮼s correcto? Di s칤 o no.`);
        break;
      case 'apellido':
        hablar('Ahora por favor, di tu apellido.');
        break;
      case 'confirmarApellido':
        hablar(`Dijiste que tu apellido es: ${datoTemporal}. 쮼s correcto? Di s칤 o no.`);
        break;
      case 'correo':
        hablar('Ahora indica tu correo electr칩nico. Di tu correo reemplazando el arroba por la palabra "arroba".');
        break;
      case 'confirmarCorreo':
        const correoFormateado = datoTemporal.replace(' arroba ', '@').replace(/\s/g, '');
        hablar(`Dijiste que tu correo es: ${correoFormateado}. 쮼s correcto? Di s칤 o no.`);
        break;
      case 'password':
        hablar('Ahora indica tu contrase침a. Debe tener al menos 6 caracteres.');
        break;
      case 'confirmarPassword':
        hablar('Por favor, repite tu contrase침a para confirmar.');
        break;
      case 'discapacidad':
        hablar('Por favor, di el tipo de discapacidad visual que tienes. Opciones: ceguera total, baja visi칩n, visi칩n borrosa o fotofobia.');
        break;
      case 'descripcion':
        hablar('쮻eseas agregar una descripci칩n adicional? Di no si no es necesario.');
        break;
      case 'registrar':
        hablar('Registrando tu informaci칩n.');
        manejarEnvioFormulario();
        break;
      default:
        break;
    }
  };

  const manejarResultadoVoz = (resultado) => {
    const esSi = ['s칤', 'si'].includes(resultado);
    const esNo = resultado === 'no';

    switch (pasoActual) {
      case 'inicio':
        setDatoTemporal(resultado);
        iniciarPaso('confirmarNombre');
        break;

      case 'confirmarNombre':
        if (esSi) {
          setFormData(prev => ({ ...prev, nombre: datoTemporal }));
          nombreRef.current?.focus(); // 游녣 Enfocar campo
          iniciarPaso('apellido');
        } else if (esNo) {
          iniciarPaso('inicio');
        } else {
          hablar(`${datoTemporal} es tu nombre? Di s칤 o no.`);
        }
        break;

      case 'apellido':
        setDatoTemporal(resultado);
        iniciarPaso('confirmarApellido');
        break;

      case 'confirmarApellido':
        if (esSi) {
          setFormData(prev => ({ ...prev, apellido: datoTemporal }));
          apellidoRef.current?.focus();
          iniciarPaso('correo');
        } else if (esNo) {
          iniciarPaso('apellido');
        } else {
          hablar(`${datoTemporal} es tu apellido? Di s칤 o no.`);
        }
        break;

      case 'correo':
        setDatoTemporal(resultado);
        iniciarPaso('confirmarCorreo');
        break;

      case 'confirmarCorreo':
        if (esSi) {
          const correoFinal = datoTemporal.replace(' arroba ', '@').replace(/\s/g, '');
          setFormData(prev => ({ ...prev, correo: correoFinal }));
          correoRef.current?.focus();
          iniciarPaso('password');
        } else if (esNo) {
          iniciarPaso('correo');
        } else {
          const correoFormateado = datoTemporal.replace(' arroba ', '@').replace(/\s/g, '');
          hablar(`${correoFormateado} es tu correo? Di s칤 o no.`);
        }
        break;

      case 'password':
        setDatoTemporal(resultado);
        iniciarPaso('confirmarPassword');
        break;

      case 'confirmarPassword':
        if (esSi) {
          setFormData(prev => ({ ...prev, password: datoTemporal, confirmPassword: datoTemporal }));
          passwordRef.current?.focus();
          confirmPasswordRef.current?.focus(); // opcional, si quieres que vea ambos
          iniciarPaso('discapacidad');
        } else if (esNo) {
          iniciarPaso('password');
        } else {
          hablar(`쮼sa contrase침a es correcta? Di s칤 o no.`);
        }
        break;

      case 'discapacidad':
        if (['ceguera total', 'baja visi칩n', 'visi칩n borrosa', 'fotofobia'].includes(resultado)) {
          setFormData(prev => ({ ...prev, discapacidad: resultado }));
          discapacidadRef.current?.focus();
          iniciarPaso('descripcion');
        } else {
          hablar('No reconoc칤 ese tipo de discapacidad. Di: ceguera total, baja visi칩n, visi칩n borrosa o fotofobia.');
        }
        break;

      case 'descripcion':
        if (esNo) {
          iniciarPaso('registrar');
        } else {
          setFormData(prev => ({ ...prev, descripcion: resultado }));
          descripcionRef.current?.focus();
          iniciarPaso('registrar');
        }
        break;

      default:
        break;
    }
  };

  const iniciarReconocimiento = () => {
    if (cargando) return;
    const recognition = recognitionRef.current;
    if (!recognition) {
      hablar("El reconocimiento de voz no est치 disponible.");
      return;
    }

    try {
      recognition.start();
      console.log("Reconocimiento iniciado.");
    } catch (error) {
      console.error("Error al iniciar reconocimiento:", error);
      hablar("Hubo un error con el micr칩fono. Por favor, intenta nuevamente.");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const manejarEnvioFormulario = async () => {
    if (correoExistente) {
      hablar('Este correo ya est치 registrado. Intenta con otro.', () => iniciarPaso('correo'));
      return;
    }

    if (formData.password.length < 6) {
      hablar('La contrase침a debe tener al menos 6 caracteres.', () => iniciarPaso('password'));
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      hablar('Las contrase침as no coinciden.', () => iniciarPaso('password'));
      return;
    }

    if (!formData.discapacidad) {
      hablar('Debes seleccionar un tipo de discapacidad.', () => iniciarPaso('discapacidad'));
      return;
    }

    try {
      const res = await fetch('http://localhost:4000/api/RegistroDiscapacitados', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      if (res.ok) {
        setMensajeEnviado(true);
        hablar('춰Registro exitoso! Ser치s redirigido al inicio de sesi칩n.', () => navigate('/login'));
      } else {
        const errorData = await res.json();
        hablar(`Error al registrar: ${errorData.message || 'Intenta m치s tarde'}`);
      }
    } catch (err) {
      console.error('Error:', err);
      hablar('Ocurri칩 un error al conectar con el servidor. Intenta m치s tarde.');
    }
  };

  return (
    <>
      <BotonesModoOscuroVolver modoOscuro={modoOscuro} setModoOscuro={setModoOscuro} />

      <div className="contenedor-microfono">
        <button
          aria-label="Activar micr칩fono para dictar informaci칩n"
          aria-describedby="instruccion-mic"
          onClick={iniciarReconocimiento}
          disabled={cargando}
          className={`boton-microfono ${reconocimientoActivo ? 'activo' : ''}`}>
          <img
            src={reconocimientoActivo 
              ? "https://cdn-icons-png.flaticon.com/512/4712/4712035.png" 
              : "https://cdn-icons-png.flaticon.com/512/4712/4712109.png"}
            alt={reconocimientoActivo ? "Micr칩fono activo" : "Micr칩fono inactivo"}
            className="icono-microfono"
            aria-hidden="true"
          />
        </button>
        <span id="instruccion-mic" hidden>
          Presiona este bot칩n para activar el micr칩fono. Luego, sigue las instrucciones habladas.
        </span>
        {cargando && <div className="cargando-voz">Procesando...</div>}
      </div>

      <div className="registro-container">
        <form onSubmit={e => e.preventDefault()}>
          <h2>Formulario de Registro</h2>

          <label htmlFor="nombre">Nombre</label>
          <input type="text" id="nombre" name="nombre" value={formData.nombre} onChange={handleChange} required />

          <label htmlFor="apellido">Apellido</label>
          <input type="text" id="apellido" name="apellido" value={formData.apellido} onChange={handleChange} required />

          <label htmlFor="correo">Correo electr칩nico: 
            {correoExistente && <span className="correo-registrado"> * Este correo ya est치 registrado</span>}
            {validandoCorreo && <span className="validando-correo"> (Validando...)</span>}
          </label>
          <input type="email" id="correo" name="correo" value={formData.correo} onChange={handleChange} required />

          <label htmlFor="password">Contrase침a (m칤nimo 6 caracteres)</label>
          <input type="password" id="password" name="password" value={formData.password} onChange={handleChange} required />

          <label htmlFor="confirmPassword">Confirmar contrase침a</label>
          <input type="password" id="confirmPassword" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} required />

          <label htmlFor="discapacidad">Tipo de discapacidad</label>
          <select id="discapacidad" name="discapacidad" value={formData.discapacidad} onChange={handleChange} required>
            <option value="">Seleccione un tipo de discapacidad visual</option>
            <option value="cegueraTotal">Ceguera total</option>
            <option value="bajaVision">Baja visi칩n</option>
            <option value="visionBorrosa">Visi칩n borrosa</option>
            <option value="fotofobia">Fotofobia</option>
          </select>

          <label htmlFor="descripcion">Descripci칩n adicional (opcional)</label>
          <textarea id="descripcion" name="descripcion" value={formData.descripcion} onChange={handleChange} rows="4" />

          <div className="botones-envio">
            <button 
              type="button" 
              className="btn-enviar" 
              onClick={manejarEnvioFormulario}
              disabled={cargando}>
              {cargando ? 'Procesando...' : 'Registrarse'}
            </button>
            <div className="texto-cuenta">
              쯏a tienes una cuenta?{' '}
              <button 
                type="button" 
                className="btn-iniciar-sesion" 
                onClick={() => navigate('/login')}
                disabled={cargando}>
                Inicia sesi칩n
              </button>
            </div>
          </div>

          {mensajeEnviado && <p className="mensaje-exito">춰Registro exitoso!</p>}
        </form>
      </div>
    </>
  );
}

export default FormularioDiscapacitados;
