import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import BotonesModoOscuroVolver from '../components/BotonesModoOscuroVolver';
import { verificarCorreo } from '../utils/verificarCorreo';
import '../styles/light.css';
import '../stylesdark/dark.css';

function FormularioConductores() {
  const [formData, setFormData] = useState({
    nombreCompleto: "",
    documento: "",
    licenciaArchivo: null,
    placa: "",
    tipoVehiculo: "",
    soat: "",
    revision: "",
    telefono: "",
    correo: "",
    zona: "",
    password: "",
    confirmPassword: ""
  });

  const [errores, setErrores] = useState({
    nombreCompleto: "",
    documento: "",
    placa: "",
    telefono: "",
    soat: "",
    password: "",
    confirmPassword: ""
  });

  const [modoOscuro, setModoOscuro] = useState(false);
  const [mensajeEnviado, setMensajeEnviado] = useState(false);
  const [errorRevision, setErrorRevision] = useState("");
  const [correoExistente, setCorreoExistente] = useState(false);
  const [validandoCorreo, setValidandoCorreo] = useState(false);
  const [fechaMin, setFechaMin] = useState('');
  const [fechaMax, setFechaMax] = useState('');
  const navigate = useNavigate();

  // Validaciones
  const validarNombre = (nombre) => {
    if (!nombre) return "Este campo es requerido";
    if (!/^[a-zA-ZáéíóúÁÉÍÓÚñÑ\s]+$/.test(nombre)) {
      return "Solo se permiten letras y espacios";
    }
    return "";
  };

  const validarDocumento = (doc) => {
    if (!doc) return "Este campo es requerido";
    if (!/^\d+$/.test(doc)) {
      return "Solo se permiten números";
    }
    return "";
  };

  const validarPlaca = (placa) => {
    if (!placa) return "Este campo es requerido";
    if (!/^[a-zA-Z]{2,4}\d{2,4}$/.test(placa)) {
      return "Formato inválido (ej: ABC123 o AB123)";
    }
    return "";
  };

  const validarTelefono = (tel) => {
    if (!tel) return "Este campo es requerido";
    if (!/^\d{10}$/.test(tel)) {
      return "Debe tener 10 dígitos";
    }
    return "";
  };

  const validarSOAT = (soat) => {
    if (!soat) return "Este campo es requerido";
    if (!/^[a-zA-Z0-9]{10,20}$/.test(soat)) {
      return "Formato inválido (10-20 caracteres alfanuméricos)";
    }
    return "";
  };

  const validarPassword = (pass) => {
    if (!pass) return "Este campo es requerido";
    if (/\s/.test(pass)) {
      return "No se permiten espacios";
    }
    if (pass.length < 6) {
      return "Mínimo 6 caracteres";
    }
    return "";
  };

  const validarConfirmPassword = (confirmPass) => {
    if (!confirmPass) return "Este campo es requerido";
    if (confirmPass !== formData.password) {
      return "Las contraseñas no coinciden";
    }
    return "";
  };

 useEffect(() => {
  const validarConfirmPassword = (confirmPass) => {
    if (!confirmPass) return "Este campo es requerido";
    if (confirmPass !== formData.password) {
      return "Las contraseñas no coinciden";
    }
    return "";
  };

  if (formData.confirmPassword) {
    const error = validarConfirmPassword(formData.confirmPassword);
    setErrores(prev => ({...prev, confirmPassword: error}));
  }
}, [formData.password, formData.confirmPassword]);

  useEffect(() => {
    const timer = setTimeout(() => {
      verificarCorreo(formData.correo, setCorreoExistente, setValidandoCorreo);
    }, 500);
    return () => clearTimeout(timer);
  }, [formData.correo]);

  useEffect(() => {
    document.body.classList.toggle('dark', modoOscuro);

    const hoy = new Date();
    const manana = new Date();
    manana.setDate(hoy.getDate() + 1);

    const haceUnAno = new Date();
    haceUnAno.setFullYear(hoy.getFullYear() - 1);

    const formato = (fecha) => fecha.toISOString().split('T')[0];

    const min = formato(haceUnAno);
    const max = formato(manana);

    setFechaMin(min);
    setFechaMax(max);

    setFormData((prevData) => ({
      ...prevData,
      revision: max
    }));
  }, [modoOscuro]);

  const handleChange = (e) => {
  const { name, value, files } = e.target;

  // Primero actualizamos el estado sin validación
  setFormData({
    ...formData,
    [name]: files ? files[0] : value
  });

  // Luego validamos y actualizamos los errores
  let error = "";
  switch(name) {
    case "nombreCompleto":
      error = validarNombre(value);
      break;
    case "documento":
      error = validarDocumento(value);
      break;
    case "placa":
      error = validarPlaca(value);
      break;
    case "telefono":
      error = validarTelefono(value);
      break;
    case "soat":
      error = validarSOAT(value);
      break;
    case "password":
      error = validarPassword(value);
      break;
    case "confirmPassword":
      error = validarConfirmPassword(value);
      break;
    default:
      break;
  }

  setErrores({
    ...errores,
    [name]: error
  });
};

  const camposRequeridos = () => {
    return (
      formData.nombreCompleto &&
      formData.documento &&
      formData.licenciaArchivo &&
      formData.placa &&
      formData.tipoVehiculo &&
      formData.soat &&
      formData.revision &&
      formData.telefono &&
      formData.correo &&
      formData.zona &&
      formData.password &&
      formData.confirmPassword
    );
  };

  const manejarEnvioFormulario = async () => {
    if (correoExistente) {
      alert('Este correo ya está registrado. Por favor usa otro.');
      return;
    }

    const fechaRevision = new Date(formData.revision);
    const fechaMinDate = new Date(fechaMin);
    const fechaMaxDate = new Date(fechaMax);

    if (fechaRevision < fechaMinDate || fechaRevision > fechaMaxDate) {
      setErrorRevision(`La fecha debe estar entre ${fechaMin} y ${fechaMax}.`);
      return;
    } else {
      setErrorRevision("");
    }

    const data = new FormData();
    for (let key in formData) {
      data.append(key, formData[key]);
    }

    try {
      const response = await fetch('http://localhost:4000/api/RegistroConductores', {
        method: 'POST',
        body: data
      });

      if (response.ok) {
        setMensajeEnviado(true);
        setTimeout(() => {
          setMensajeEnviado(false);
          setFormData({
            nombreCompleto: "",
            documento: "",
            licenciaArchivo: null,
            placa: "",
            tipoVehiculo: "",
            soat: "",
            revision: "",
            telefono: "",
            correo: "",
            zona: "",
            password: "",
            confirmPassword: ""
          });
          navigate('/login'); 
        }, 1000);
      } else {
        alert("Error al registrar conductor.");
      }
    } catch (err) {
      console.error("Error en la conexión:", err);
      alert("Error en la conexión con el servidor.");
    }
  };

  const hayErrores = Object.values(errores).some(error => error !== "");
  const formularioIncompleto = !camposRequeridos();

  return (
    <>
      <BotonesModoOscuroVolver modoOscuro={modoOscuro} setModoOscuro={setModoOscuro} />
      <div className="registro-container">
        <form onSubmit={(e) => e.preventDefault()}>
          <h2>Formulario de Registro</h2>

          <label>Nombre completo {errores.nombreCompleto && <span className="error-campo">* {errores.nombreCompleto}</span>}</label>
          <input 
            type="text" 
            name="nombreCompleto" 
            value={formData.nombreCompleto}
            onChange={(e) => {
              const valor = e.target.value.replace(/[^a-zA-ZáéíóúÁÉÍÓÚñÑ\s]/g, '');
              setFormData({ ...formData, nombreCompleto: valor });
            }}
            onKeyDown={(e) => {
              if (e.key === '  ' || e.key === 'Enter') e.preventDefault();
            }}
            maxLength={50}
            required
          />

          <label>Documento de identidad {errores.documento && <span className="error-campo">* {errores.documento}</span>}</label>
          <input 
            type="text" 
            name="documento" 
            value={formData.documento}
            onChange={(e) => {
              const valor = e.target.value.replace(/\D/g, ''); // Solo dígitos
              setFormData({ ...formData, documento: valor });
            }}
            maxLength={15}
            required
          />

          <label>Adjuntar licencia de conducción</label>
          <input 
            type="file" 
            name="licenciaArchivo" 
            accept="application/pdf,image/*" 
            onChange={handleChange} 
            required 
          />

          <label>Número de placa del vehículo {errores.placa && <span className="error-campo">* {errores.placa}</span>}</label>
          <input 
            type="text" 
            name="placa" 
            value={formData.placa}
            onChange={(e) => {
              const valor = e.target.value.toUpperCase().replace(/[^a-zA-Z0-9]/g, '');
              setFormData({ ...formData, placa: valor });
            }}
            maxLength={6}
            required
            placeholder="ABC123"
          />

          <label>Tipo de vehículo</label>
          <input 
            type="tel" 
            name="telefono" 
            value={formData.telefono}
            onChange={(e) => {
              const valor = e.target.value.replace(/\D/g, '');
              setFormData({ ...formData, telefono: valor });
            }}
            maxLength={10}
            required
          />

          <label>Seguro obligatorio (SOAT) {errores.soat && <span className="error-campo">* {errores.soat}</span>}</label>
          <input 
            type="text" 
            name="soat" 
            value={formData.soat}
            onChange={(e) => {
              const valor = e.target.value.replace(/[^a-zA-Z0-9]/g, '');
              setFormData({ ...formData, soat: valor });
            }}
            maxLength={20}
            required
          />

          <label>Última fecha de revisión técnico-mecánica</label>
          <input
            type="date"
            name="revision"
            value={formData.revision}
            onChange={handleChange}
            required
            min={fechaMin}
            max={fechaMax}
          />
          {errorRevision && <p className="mensaje-error">{errorRevision}</p>}

          <label>Teléfono {errores.telefono && <span className="error-campo">* {errores.telefono}</span>}</label>
          <input 
            type="tel" 
            name="telefono" 
            value={formData.telefono} 
            onChange={handleChange} 
            required 
          />

          <label htmlFor="correo">
            Correo electrónico:
            {correoExistente && <span className="correo-registrado"> * Este correo ya está registrado</span>}
            {validandoCorreo && <span className="validando-correo"> Validando correo...</span>}
          </label>
          <input 
            type="email" 
            id="correo" 
            name="correo" 
            value={formData.correo} 
            onChange={handleChange} 
            required 
          />

          <label>Contraseña {errores.password && <span className="error-campo">* {errores.password}</span>}</label>
          <input 
            type="password" 
            name="password" 
            value={formData.password}
            onChange={(e) => {
              const valor = e.target.value.replace(/\s/g, '');
              setFormData({ ...formData, password: valor });
            }}
            onKeyDown={(e) => {
              if (e.key === ' ') e.preventDefault();
            }}
            minLength={6}
            required
          />

          <label>Confirmar contraseña {errores.confirmPassword && <span className="error-campo">* {errores.confirmPassword}</span>}</label>
          <input 
            type="password" 
            name="confirmPassword" 
            value={formData.confirmPassword}
            onChange={(e) => {
              const valor = e.target.value.replace(/\s/g, '');
              setFormData({ ...formData, confirmPassword: valor });
            }}
            onKeyDown={(e) => {
              if (e.key === ' ') e.preventDefault();
            }}
            minLength={6}
            required
          />

          <label>Zona de cobertura</label>
          <input 
            type="text" 
            name="zona" 
            value={formData.zona} 
            onChange={handleChange} 
            required 
          />

          <div className="botones-envio">
            <button 
              type="button" 
              className="btn-enviar" 
              onClick={manejarEnvioFormulario}
              disabled={hayErrores || formularioIncompleto}
            >
              Registrarse
            </button>
            <div className="texto-cuenta">
              ¿Ya tienes una cuenta?{' '}
              <button type="button" className="btn-iniciar-sesion" onClick={() => navigate('/login')}>
                Inicia sesión
              </button>
            </div>
          </div>
          {mensajeEnviado && <div className="mensaje-exito">¡Registro exitoso!</div>}
        </form>
      </div>
    </>
  );
}

export default FormularioConductores;
