import React from 'react';
import { Link } from 'react-router-dom';
import './styles/Home.css';

const Home = () => {
  return (
    <div className="home-container">
      {/* Barra de navegación */}
      <nav className="navbar">
        <div className="navbar-logo">
          <span>🚌 NaviCali</span>
        </div>
        <div className="navbar-links">
          <Link to="/registro" className="nav-button">Registrarse</Link>
          <Link to="/login" className="btn-nav btn-secondary">Iniciar Sesión</Link>
        </div>
      </nav>

      {/* Sección principal */}
      <header className="hero-section">
        <h1 className="hero-title">Bienvenido a la App de Navegación Accesible</h1>
        <p className="hero-subtitle">
          Conecta personas con discapacidad visual, acompañantes y conductores para una movilidad más inclusiva en Cali.
        </p>
        
      </header>

      <section className="info-section">
        <h2>¿Cómo funciona?</h2>
        <p>
          Nuestra plataforma permite que las personas con discapacidad visual accedan a rutas de transporte público 
          con ayuda de acompañantes o conductores registrados. Todo está diseñado para ser accesible, confiable y fácil de usar.
        </p>
      </section>
    </div>
  );
};

export default Home;
