import React, { useRef, useEffect } from 'react';
import mapboxgl from 'mapbox-gl';
import './styles/MapaCali.css';
import { Link } from 'react-router-dom';

mapboxgl.accessToken = 'TU_TOKEN_DE_MAPBOX'; // Reemplaza con tu token real

const Mapa = () => {
  const mapaRef = useRef(null);

  useEffect(() => {
    const map = new mapboxgl.Map({
      container: mapaRef.current,
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [-76.5225, 3.4516], // Centro de Cali
      zoom: 13
    });

    map.on('load', () => {
      map.addSource('rutas-mio', {
        type: 'geojson',
        data: '/geojson/rutas_mio.geojson'
      });

      map.addLayer({
        id: 'rutas-linea',
        type: 'line',
        source: 'rutas-mio',
        paint: {
          'line-color': '#ff6600',
          'line-width': 3
        },
        filter: ['==', '$type', 'LineString']
      });

      map.addLayer({
        id: 'estaciones',
        type: 'circle',
        source: 'rutas-mio',
        paint: {
          'circle-radius': 6,
          'circle-color': '#004b8d'
        },
        filter: ['==', '$type', 'Point']
      });

      map.addLayer({
        id: 'etiquetas-estaciones',
        type: 'symbol',
        source: 'rutas-mio',
        layout: {
          'text-field': ['get', 'name'],
          'text-size': 12,
          'text-offset': [0, 1.2],
          'text-anchor': 'top'
        },
        paint: {
          'text-color': '#004b8d'
        },
        filter: ['==', '$type', 'Point']
      });
    });

    return () => map.remove();
  }, []);

  return (
    <div className="mapa-page">
      <div className="mapa-container" ref={mapaRef} />
      <div className="boton-volver-container">
        <Link to="/" className="boton-volver">Volver al Inicio</Link>
      </div>
    </div>
  );
};

export default Mapa;
