import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import BotonesModoOscuroVolver from '../components/BotonesModoOscuroVolver';
import { verificarCorreo } from '../utils/verificarCorreo';
import '../styles/light.css';
import '../styles/dark.css';

function FormularioDiscapacitados() {
  const [formData, setFormData] = useState({
    nombre: '',
    apellido: '',
    correo: '',
    password: '',
    confirmPassword: '',
    discapacidad: '',
    descripcion: ''
  });

  const [modoOscuro, setModoOscuro] = useState(false);
  const [correoExistente, setCorreoExistente] = useState(false);
  const [validandoCorreo, setValidandoCorreo] = useState(false);
  const [pasoActual, setPasoActual] = useState('inicio');
  const [reconocimientoActivo, setReconocimientoActivo] = useState(false);
  const [mensajeEnviado, setMensajeEnviado] = useState(false);
  const repetirTimer = useRef(null);
  const [cargando, setCargando] = useState(false);
  const navigate = useNavigate();
  const recognitionRef = useRef(null);

  const nombreRef = useRef(null);
  const apellidoRef = useRef(null);
  const correoRef = useRef(null);
  const passwordRef = useRef(null);
  const confirmPasswordRef = useRef(null);
  const discapacidadRef = useRef(null);
  const descripcionRef = useRef(null);


  useEffect(() => {
    document.body.classList.toggle('dark', modoOscuro);

    if ('webkitSpeechRecognition' in window) {
      const recognition = new window.webkitSpeechRecognition();
      recognition.lang = 'es-ES';
      recognition.continuous = false;
      recognition.interimResults = false;

      recognition.onresult = (event) => {
        const resultado = event.results?.[0]?.[0]?.transcript.trim().toLowerCase();
        if (resultado) {
          console.log("Texto reconocido:", resultado);
          manejarResultadoVoz(resultado);
        }
      };

      recognition.onerror = (event) => {
        console.error('Error en reconocimiento:', event.error);
        hablar('Hubo un error con el micrófono. Por favor intenta nuevamente.');
      };

      recognitionRef.current = recognition;
    }

    iniciarPaso('inicio');

    return () => {
      if (repetirTimer) clearTimeout(repetirTimer);
      recognitionRef.current?.abort();
    };
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [modoOscuro]); 

  useEffect(() => {
    const timer = setTimeout(() => {
      if (formData.correo) {
        verificarCorreo(formData.correo, setCorreoExistente, setValidandoCorreo);
      }
    }, 500);
    return () => clearTimeout(timer);
  }, [formData.correo]);

  const hablar = (texto, callback, continuarConVoz = true) => {
    if (cargando) return;

    setReconocimientoActivo(false);
    recognitionRef.current?.abort();
    clearTimeout(repetirTimer.current);

    setCargando(true);
    const mensaje = new SpeechSynthesisUtterance(texto);
    mensaje.lang = 'es-ES';

    mensaje.onend = () => {
      setCargando(false);
      if (callback) callback();
      if (continuarConVoz) {
        setTimeout(() => {
          iniciarReconocimiento();
        }, 500);
      }
    };

    window.speechSynthesis.cancel();
    window.speechSynthesis.speak(mensaje);
  };

  const iniciarPaso = (paso) => {
  setPasoActual(paso);

  switch (paso) {
    case 'inicio':
      hablar('Este es el formulario de registro para discapacitados visuales. Por favor, di tu nombre.', null, true);
      nombreRef.current?.focus();
      break;

    case 'apellido':
      hablar('Ahora por favor, di tu apellido.', null, true);
      apellidoRef.current?.focus();
      break;

    case 'correo':
      hablar('Ahora indica tu correo electrónico. Di tu correo reemplazando el arroba por la palabra arroba.', null, true);
      correoRef.current?.focus();
      break;

    case 'password':
      hablar('Ahora indica tu contraseña. Debe tener al menos 6 caracteres.', null, true);
      passwordRef.current?.focus();
      break;

    case 'discapacidad':
      hablar('Por favor, di el tipo de discapacidad visual que tienes. Opciones: ceguera total, baja visión, visión borrosa o fotofobia.', null, true);
      discapacidadRef.current?.focus();
      break;

    case 'descripcion':
      hablar('¿Deseas agregar una descripción adicional? Di no si no es necesario.', null, true);
      descripcionRef.current?.focus();
      break;

    case 'registrar':
      hablar('Registrando tu información.', () => manejarEnvioFormulario(), false);
      break;

    default:
      break;
  }
};

  const manejarResultadoVoz = (resultado) => {
  console.log(`Resultado en paso ${pasoActual}:`, resultado);

  const mapDiscapacidad = {
    'ceguera total': 'cegueraTotal',
    'baja visión': 'bajaVision',
    'visión borrosa': 'visionBorrosa',
    'fotofobia': 'fotofobia'
  };

  switch (pasoActual) {
    case 'inicio':
      setFormData(prev => ({ ...prev, nombre: resultado }));
      hablar('Ahora por favor, di tu apellido.', () => iniciarPaso('apellido'));
      break;

    case 'apellido':
      setFormData(prev => ({ ...prev, apellido: resultado }));
      hablar('Ahora indica tu correo electrónico. Di tu correo reemplazando el arroba por la palabra arroba.', () => iniciarPaso('correo'));
      break;

    case 'correo':
      const correo = resultado.replace(' arroba ', '@').replace(/\s/g, '');
      setFormData(prev => ({ ...prev, correo }));
      hablar('Ahora indica tu contraseña. Debe tener al menos 6 caracteres.', () => iniciarPaso('password'));
      break;

    case 'password':
      setFormData(prev => ({ ...prev, password: resultado, confirmPassword: resultado }));
      hablar('Por favor, di el tipo de discapacidad visual que tienes. Opciones: ceguera total, baja visión, visión borrosa o fotofobia.', () => iniciarPaso('discapacidad'));
      break;

    case 'discapacidad':
      if (mapDiscapacidad[resultado]) {
        setFormData(prev => ({
          ...prev,
          discapacidad: mapDiscapacidad[resultado]
        }));
        hablar('¿Deseas agregar una descripción adicional? Di no si no es necesario.', () => iniciarPaso('descripcion'));
      } else {
        hablar('No reconocí ese tipo. Di: ceguera total, baja visión, visión borrosa o fotofobia.', null, true);
      }
      break;

    case 'descripcion':
      if (['no', 'ninguna', 'nada'].includes(resultado)) {
        iniciarPaso('registrar');
      } else {
        setFormData(prev => ({ ...prev, descripcion: resultado }));
        iniciarPaso('registrar');
      }
      break;

    default:
      break;
  }
};

  const iniciarReconocimiento = () => {
    if (cargando) return;

    const recognition = recognitionRef.current;
    if (!recognition) {
      hablar("El reconocimiento de voz no está disponible.");
      return;
    }

    try {
      recognition.start();
      console.log('Reconocimiento iniciado para paso:', pasoActual);
    } catch (error) {
      console.error('Error al iniciar reconocimiento:', error);
      hablar('Hubo un error con el micrófono. Por favor, intenta nuevamente.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const manejarEnvioFormulario = async () => {
    if (correoExistente) return hablar('Este correo ya está registrado.', () => iniciarPaso('correo'));
    if (formData.password.length < 6) return hablar('La contraseña debe tener al menos 6 caracteres.', () => iniciarPaso('password'));
    if (formData.password !== formData.confirmPassword) return hablar('Las contraseñas no coinciden.', () => iniciarPaso('password'));
    if (!formData.discapacidad) return hablar('Debes seleccionar un tipo de discapacidad.', () => iniciarPaso('discapacidad'));

    try {
      const res = await fetch('http://localhost:4000/api/RegistroDiscapacitados', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      if (res.ok) {
        setMensajeEnviado(true);
        hablar('¡Registro exitoso! Serás redirigido al inicio de sesión.', () => navigate('/login'));
      } else {
        const err = await res.json();
        hablar(`Error al registrar: ${err.message || 'Intenta más tarde'}`);
      }
    } catch (err) {
      console.error(err);
      hablar('Ocurrió un error al conectar con el servidor. Intenta más tarde.');
    }
  };

  return (
    <>
      <BotonesModoOscuroVolver modoOscuro={modoOscuro} setModoOscuro={setModoOscuro} />

      <div className="contenedor-microfono">
        <button
          aria-label="Activar micrófono para dictar información"
          aria-describedby="instruccion-mic"
          onClick={iniciarReconocimiento} // 👈 sin condición
          disabled={cargando}
          className={`boton-microfono ${reconocimientoActivo ? 'activo' : ''}`}>
          <img
            src={reconocimientoActivo 
              ? "https://cdn-icons-png.flaticon.com/512/4712/4712035.png" 
              : "https://cdn-icons-png.flaticon.com/512/4712/4712109.png"}
            alt={reconocimientoActivo ? "Micrófono activo" : "Micrófono inactivo"}
            className="icono-microfono"
            aria-hidden="true"
          />
        </button>
        <span id="instruccion-mic" hidden>
          Presiona este botón para activar el micrófono. Luego, sigue las instrucciones habladas.
        </span>
        {cargando && <div className="cargando-voz">Procesando...</div>}
      </div>

      <div className="registro-container">
        <form onSubmit={e => e.preventDefault()}>
          <h2>Formulario de Registro</h2>

          <label htmlFor="nombre">Nombre</label>
          <input 
            ref={nombreRef}
            type="text" 
            id="nombre" 
            name="nombre" 
            value={formData.nombre} 
            onChange={handleChange} 
            required 
          />

          <label htmlFor="apellido">Apellido</label>
          <input 
            ref={apellidoRef}
            type="text" 
            id="apellido" 
            name="apellido" 
            value={formData.apellido} 
            onChange={handleChange} 
            required 
          />

          <label htmlFor="correo">Correo electrónico: 
            {correoExistente && <span className="correo-registrado"> * Este correo ya está registrado</span>}
            {validandoCorreo && <span className="validando-correo"> (Validando...)</span>}
          </label>
          <input 
            ref={correoRef}
            type="email" 
            id="correo" 
            name="correo" 
            value={formData.correo} 
            onChange={handleChange} 
            required 
          />

          <label htmlFor="password">Contraseña (mínimo 6 caracteres)</label>
          <input 
            ref={passwordRef}
            type="password" 
            id="password" 
            name="password" 
            value={formData.password} 
            onChange={handleChange} 
            required 
          />

          <label htmlFor="confirmPassword">Confirmar contraseña</label>
          <input 
            ref={confirmPasswordRef}
            type="password" 
            id="confirmPassword" 
            name="confirmPassword" 
            value={formData.confirmPassword} 
            onChange={handleChange} 
            required 
          />

          <label htmlFor="discapacidad">Tipo de discapacidad</label>
          <select 
            ref={discapacidadRef}
            id="discapacidad" 
            name="discapacidad" 
            value={formData.discapacidad} 
            onChange={handleChange} 
            required
          >
            <option value="">Seleccione un tipo de discapacidad visual</option>
            <option value="cegueraTotal">Ceguera total</option>
            <option value="bajaVision">Baja visión</option>
            <option value="visionBorrosa">Visión borrosa</option>
            <option value="fotofobia">Fotofobia</option>
          </select>

          <label htmlFor="descripcion">Descripción adicional (opcional)</label>
          <textarea 
            ref={descripcionRef}
            id="descripcion" 
            name="descripcion" 
            value={formData.descripcion} 
            onChange={handleChange} 
            rows="4" 
          />

          <div className="botones-envio">
            <button 
              type="button" 
              className="btn-enviar" 
              onClick={manejarEnvioFormulario}
              disabled={cargando}>
              {cargando ? 'Procesando...' : 'Registrarse'}
            </button>
            <div className="texto-cuenta">
              ¿Ya tienes una cuenta?{' '}
              <button 
                type="button" 
                className="btn-iniciar-sesion" 
                onClick={() => navigate('/login')}
                disabled={cargando}>
                Inicia sesión
              </button>
            </div>
          </div>

          {mensajeEnviado && <p className="mensaje-exito">¡Registro exitoso!</p>}
        </form>
      </div>
    </>
  );
}

export default FormularioDiscapacitados;
