import React, { useState, useEffect } from 'react';
import './styles/light.css';
import './styles/dark.css';

function FormularioConductores() {
  const [formData, setFormData] = useState({
    nombreCompleto: "",
    documento: "",
    licenciaArchivo: null,
    placa: "",
    tipoVehiculo: "",
    soat: "",
    revision: "",
    telefono: "",
    correo: "",
    zona: "",
    password: "", // Nuevo campo
    confirmPassword: "" // Nuevo campo
  });

  const [modoOscuro, setModoOscuro] = useState(false);
  const [mensajeEnviado, setMensajeEnviado] = useState(false);
  const [errorRevision, setErrorRevision] = useState("");

  const [fechaMin, setFechaMin] = useState('');
  const [fechaMax, setFechaMax] = useState('');

  const passwordRef = React.useRef(null);

  useEffect(() => {
    document.body.classList.toggle('dark', modoOscuro);

    const hoy = new Date();
    const manana = new Date();
    manana.setDate(hoy.getDate() + 1);

    const haceUnAno = new Date();
    haceUnAno.setFullYear(hoy.getFullYear() - 1);

    const formato = (fecha) => fecha.toISOString().split('T')[0];

    const min = formato(haceUnAno);
    const max = formato(manana);

    setFechaMin(min);
    setFechaMax(max);

    // 👇 Actualiza el campo revisión con el valor máximo permitido
    setFormData((prevData) => ({
      ...prevData,
      revision: max
    }));
  }, [modoOscuro]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData({
      ...formData,
      [name]: files ? files[0] : value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.password !== formData.confirmPassword) {
      alert('Las contraseñas no coinciden.');
      if (passwordRef.current) passwordRef.current.focus();
      return;
    }

    // Validación de fecha de revisión técnico-mecánica
    const fechaRevision = new Date(formData.revision);
    const fechaMinDate = new Date(fechaMin);
    const fechaMaxDate = new Date(fechaMax);

    if (fechaRevision < fechaMinDate || fechaRevision > fechaMaxDate) {
      setErrorRevision("La fecha debe estar entre " + fechaMin + " y " + fechaMax + ".");
      return;
    } else {
      setErrorRevision("");
    }

    const data = new FormData();
    for (let key in formData) {
      data.append(key, formData[key]);
    }

    try {
      const response = await fetch('http://localhost:4000/api/RegistroConductores', {
        method: 'POST',
        body: data
      });
      if (response.ok) {
        setMensajeEnviado(true);
        setTimeout(() => setMensajeEnviado(false), 3000);
        setFormData({
          nombreCompleto: "",
          documento: "",
          licenciaArchivo: null,
          placa: "",
          tipoVehiculo: "",
          soat: "",
          revision: "",
          telefono: "",
          correo: "",
          zona: "",
          password: "", // Reiniciar campo
          confirmPassword: "" // Reiniciar campo
        });
      } else {
        console.error("Error al registrar conductor");
      }
    } catch (err) {
      console.error("Error en la conexión:", err);
    }
  };

  return (
    <>
      {/* Botones volver al inicio y modo oscuro juntos, alineados y centrados */}
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '1.5rem', margin: '2.5rem auto 1.5rem auto', maxWidth: 520 }}>
        <button
          type="button"
          className="btn-primary"
          onClick={() => window.location.href = "/"}
          style={{
            fontWeight: 600,
            fontSize: '1.05rem',
            borderRadius: '6px',
            padding: '0.7rem 1.5rem',
            border: '2px solid #1976d2',
            background: '#1976d2',
            color: '#fff',
            cursor: 'pointer',
            maxWidth: 250,
            transition: 'background 0.2s, color 0.2s'
          }}
          aria-label="Volver al inicio"
        >
          Volver al inicio
        </button>
        <button
          type="button"
          className="btn-primary"
          onClick={() => setModoOscuro(!modoOscuro)}
          aria-pressed={modoOscuro}
          aria-label={modoOscuro ? 'Cambiar a modo claro' : 'Cambiar a modo oscuro'}
          style={{
            fontWeight: 600,
            fontSize: '1.05rem',
            borderRadius: '6px',
            padding: '0.7rem 1.5rem',
            border: '2px solid #1976d2',
            background: '#1976d2',
            color: '#fff',
            cursor: 'pointer',
            maxWidth: 250,
            transition: 'background 0.2s, color 0.2s',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            gap: '0.5rem'
          }}
        >
          {modoOscuro ? 'Modo claro' : 'Modo oscuro'}
        </button>
      </div>
      <div className="registro-container">
        <form onSubmit={handleSubmit}>
          <h2>Formulario de Registror</h2>

          <label>Nombre completo</label>
          <input type="text" name="nombreCompleto" value={formData.nombreCompleto} onChange={handleChange} required />

          <label>Documento de identidad</label>
          <input type="text" name="documento" value={formData.documento} onChange={handleChange} required />

          <label>Adjuntar licencia de conducción</label>
          <input type="file" name="licenciaArchivo" accept="application/pdf,image/*" onChange={handleChange} required />

          <label>Número de placa del vehículo</label>
          <input type="text" name="placa" value={formData.placa} onChange={handleChange} required />

          <label>Tipo de vehículo</label>
          <input type="text" name="tipoVehiculo" value={formData.tipoVehiculo} onChange={handleChange} required />

          <label>Seguro obligatorio (SOAT)</label>
          <input type="text" name="soat" value={formData.soat} onChange={handleChange} required />

          <label>Última fecha de revisión técnico-mecánica</label>
          <input
            type="date"
            name="revision"
            value={formData.revision}
            onChange={handleChange}
            required
            min={fechaMin}
            max={fechaMax}
          />
          {errorRevision && <p className="mensaje-error">{errorRevision}</p>}

          <label>Teléfono</label>
          <input type="tel" name="telefono" value={formData.telefono} onChange={handleChange} required />

          <label>Correo electrónico</label>
          <input type="email" name="correo" value={formData.correo} onChange={handleChange} required />

          <label>Contraseña</label>
          <input ref={passwordRef} type="password" name="password" value={formData.password} onChange={handleChange} required />

          <label>Confirmar contraseña</label>
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword || ''}
            onChange={handleChange}
            required
          />

          <label>Zona de cobertura</label>
          <input type="text" name="zona" value={formData.zona} onChange={handleChange} required />

          <div className="button-group" style={{ display: 'flex', flexDirection: 'column', gap: '15px', marginTop: '30px' }}>
            <button type="submit" className="btn-primary">
              Enviar
            </button>
            <button type="button" className="btn-secondary" onClick={() => window.location.href = "/login"}>
              Ya tengo cuenta - Iniciar sesión
            </button>
          </div>
          {mensajeEnviado && <p className="mensaje-exito">¡Has sido registrado correctamente!.</p>}
        </form>
      </div>
    </>
  );
}

export default FormularioConductores;
