import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/Home.css';
import fondoRecuadro from '../assets/NaviGuiaHome.jpeg';

function Home() {
  return (
    <div className="home-wrapper">
      <nav className="navbar">
        <div className="logo">
          🚆 <span>NaviCali</span>
        </div>
        <div className="buttons">
          <Link to="/inicioRegistro" className="btn">Registrarse</Link>
          <Link to="/login" className="btn">Iniciar Sesión</Link>
        </div>
      </nav>

      <main className="home-content">
        <div
          className="home-card"
          style={{
            backgroundImage: `url(${fondoRecuadro})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            borderRadius: '1rem',
            padding: '2rem',
            color: 'white',
            textShadow: '1px 1px 2px black'
          }}
        >
          <h2>Bienvenido a la App de Navegación Accesible</h2>
          <p>Conecta personas con discapacidad visual, acompañantes y conductores para una movilidad más inclusiva en Cali.</p>
          <h3>¿Cómo funciona?</h3>
          <p>
            Nuestra plataforma permite que las personas con discapacidad visual accedan a rutas de transporte público
            con ayuda de acompañantes o conductores registrados. Todo está diseñado para ser accesible, confiable y fácil de usar.
          </p>
        </div>
      </main>
    </div>
  );
}

export default Home;
