import React, { useState, useEffect } from 'react';
import './styles/light.css';
import './styles/dark.css';

function FormularioDiscapacitados() {
  const [formData, setFormData] = useState({
    nombre: "",
    apellido: "",
    correo: "",
    discapacidad: "",
    descripcion: "",
    password: "",
    confirmPassword: ""
  });

  const [modoOscuro, setModoOscuro] = useState(false);
  const [mensajeEnviado, setMensajeEnviado] = useState(false);
  const [correoError, setCorreoError] = useState("");
  const [verificandoCorreo, setVerificandoCorreo] = useState(false);
  const passwordRef = React.useRef(null);

  useEffect(() => {
    if (modoOscuro) {
      document.body.classList.add('dark');
    } else {
      document.body.classList.remove('dark');
    }
  }, [modoOscuro]);

  const verificarCorreo = async (correo) => {
    setVerificandoCorreo(true);
    try {
      const response = await fetch('http://localhost:4000/api/verificar-correo', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ correo })
      });

      if (!response.ok) throw new Error("Error en la verificación");

      const data = await response.json();
      if (data.existe) {
        setCorreoError('Este correo ya está registrado');
        return false;
      }
      setCorreoError('');
      return true;
    } catch (error) {
      console.error('Error:', error);
      setCorreoError('Error al verificar. Intente nuevamente.');
      return false;
    } finally {
      setVerificandoCorreo(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ 
      ...formData,
      [name]: value 
    });
  };

  const handleBlur = async (e) => {
    const { name, value } = e.target;

    if (name === 'correo') {
      await verificarCorreo(value);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Verificación final antes de enviar
    if (formData.correo && !await verificarCorreo(formData.correo)) {
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      alert('Las contraseñas no coinciden.');
      if (passwordRef.current) passwordRef.current.focus();
      return;
    }

    try {
      const response = await fetch('http://localhost:4000/api/RegistroDiscapacitados', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      const data = await response.json();

      if (response.ok) {
        setMensajeEnviado(true);
        setTimeout(() => {
          setMensajeEnviado(false);
        }, 3000);

        setFormData({
          nombre: "",
          apellido: "",
          correo: "",
          discapacidad: "",
          descripcion: "",
          password: "",
          confirmPassword: ""
        });
      } else {
        alert(data.message || 'Error en el registro');
      }
    } catch (error) {
      console.error("Error al conectar con el backend:", error);
      alert('Error de conexión con el servidor');
    }
  };

  return (
    <>
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '1.5rem', margin: '2.5rem auto 1.5rem auto', maxWidth: 520 }}>
        <button
          type="button"
          className="btn-primary"
          onClick={() => window.location.href = "/"}
          style={{
            fontWeight: 600,
            fontSize: '1.05rem',
            borderRadius: '6px',
            padding: '0.7rem 1.5rem',
            border: '2px solid #1976d2',
            background: '#1976d2',
            color: '#fff',
            cursor: 'pointer',
            maxWidth: 250,
            transition: 'background 0.2s, color 0.2s'
          }}
          aria-label="Volver al inicio"
        >
          Volver al inicio
        </button>
        <button
          type="button"
          className="btn-primary"
          onClick={() => setModoOscuro(!modoOscuro)}
          aria-pressed={modoOscuro}
          aria-label={modoOscuro ? 'Cambiar a modo claro' : 'Cambiar a modo oscuro'}
          style={{
            fontWeight: 600,
            fontSize: '1.05rem',
            borderRadius: '6px',
            padding: '0.7rem 1.5rem',
            border: '2px solid #1976d2',
            background: '#1976d2',
            color: '#fff',
            cursor: 'pointer',
            maxWidth: 250,
            transition: 'background 0.2s, color 0.2s',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            gap: '0.5rem'
          }}
        >
          {modoOscuro ? 'Modo claro' : 'Modo oscuro'}
        </button>
      </div>

      <div className="registro-container">
        <form onSubmit={handleSubmit}>
          <h2>Formulario de Registro</h2>

          <label htmlFor="nombre">Nombre</label>
          <input
            type="text"
            id="nombre"
            name="nombre"
            value={formData.nombre}
            onChange={handleChange}
            required
          />

          <label htmlFor="apellido">Apellido:</label>
          <input
            type="text"
            id="apellido"
            name="apellido"
            value={formData.apellido}
            onChange={handleChange}
            required
          />

          <label htmlFor="correo">Correo electrónico: <span className="error-message"> {correoError}</span></label>
            <input
              type="email"
              id="correo"
              name="correo"
              value={formData.correo}
              onChange={handleChange}
              onBlur={handleBlur}
              required
            />
          {verificandoCorreo && <p>Verificando correo...</p>}
          <label htmlFor="password">Contraseña</label>
          <input 
            ref={passwordRef} 
            type="password" 
            id="password" 
            name="password" 
            value={formData.password} 
            onChange={handleChange} 
            required 
          />

          <label htmlFor="confirmPassword">Confirmar contraseña</label>
          <input
            type="password"
            id="confirmPassword"
            name="confirmPassword"
            value={formData.confirmPassword || ''}
            onChange={handleChange}
            required
          />

          <label htmlFor="discapacidad">Tipo de discapacidad:</label>
          <select
            id="discapacidad"
            name="discapacidad"
            value={formData.discapacidad}
            onChange={handleChange}
            required
          >
            <option value="">Seleccione un tipo de discapacidad visual</option>
            <option value="cegueraTotal">Ceguera total</option>
            <option value="bajaVision">Baja visión</option>
            <option value="visionBorrosa">Visión borrosa</option>
            <option value="fotofobia">Fotofobia</option>
          </select>

          <label htmlFor="descripcion">Descripción adicional (opcional):</label>
          <textarea
            id="descripcion"
            name="descripcion"
            value={formData.descripcion}
            onChange={handleChange}
            rows="4"
          />

          <div className="button-group" style={{ display: 'flex', flexDirection: 'column', gap: '15px', marginTop: '30px' }}>
            <button 
              type="submit" 
              className="btn-primary"
              disabled={!!correoError || verificandoCorreo}
            >
              Enviar
            </button>
            <button type="button" className="btn-secondary" onClick={() => window.location.href = "/login"}>
              Ya tengo cuenta - Iniciar sesión
            </button>
          </div>

          {mensajeEnviado && (
            <p className="mensaje-exito">¡Has sido registrado correctamente!.</p>
          )}
        </form>
      </div>
    </>
  );
}

export default FormularioDiscapacitados;
