import React, { useRef, useEffect } from 'react';
import mapboxgl from 'mapbox-gl';
import './styles/MapaCali.css';
import { Link } from 'react-router-dom';

mapboxgl.accessToken = 'pk.eyJ1IjoibnlhbnRhY2F0IiwiYSI6ImNtYzNvbGVjZDA3ZDEya29scHZoY25qaGUifQ.2bOVqwSZgFeQbN_End3gyQ'; // Reemplaza con tu token real

const Mapa = () => {
  const mapaRef = useRef(null);

  useEffect(() => {
    const map = new mapboxgl.Map({
      container: mapaRef.current,
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [-76.5225, 3.4516], // Cali
      zoom: 13,
    });

    map.on('load', () => {
      map.addSource('rutas-mio', {
        type: 'geojson',
        data: '/geojson/rutas_mio.geojson',
      });

      // Líneas de rutas
      map.addLayer({
        id: 'rutas-linea',
        type: 'line',
        source: 'rutas-mio',
        paint: {
          'line-color': '#ff6600',
          'line-width': 3,
        },
        filter: ['==', '$type', 'LineString'],
      });

      // Estaciones como puntos
      map.addLayer({
        id: 'estaciones',
        type: 'circle',
        source: 'rutas-mio',
        paint: {
          'circle-radius': 6,
          'circle-color': '#004b8d',
        },
        filter: ['==', '$type', 'Point'],
      });

      // Etiquetas en las estaciones
      map.addLayer({
        id: 'etiquetas-estaciones',
        type: 'symbol',
        source: 'rutas-mio',
        layout: {
          'text-field': ['get', 'name'],
          'text-size': 12,
          'text-offset': [0, 1.2],
          'text-anchor': 'top',
        },
        paint: {
          'text-color': '#004b8d',
        },
        filter: ['==', '$type', 'Point'],
      });
    });

    return () => map.remove();
  }, []);

  return (
    <div className="mapa-container">
      <div className="mapa-box">
        <h2 className="mapa-title">Mapa de Rutas MIO en Cali</h2>
        <div className="mapbox-map" ref={mapaRef}></div>
      </div>

      <div className="botones-mapa">
        <Link to="/" className="mapa-button">Volver al Inicio</Link>
      </div>
    </div>
  );
};

export default Mapa;
