import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import BotonesModoOscuroVolver from '../components/BotonesModoOscuroVolver';
import { verificarCorreo } from '../utils/verificarCorreo';
import '../styles/light.css';
import '../styles/dark.css';

function FormularioDiscapacitados() {
  const [formData, setFormData] = useState({
    nombre: '', 
    apellido: '', 
    correo: '',
    discapacidad: '', 
    descripcion: '',
    password: '', 
    confirmPassword: ''
  });

  const [datoTemporal, setDatoTemporal] = useState('');
  const [modoOscuro, setModoOscuro] = useState(false);
  const [correoExistente, setCorreoExistente] = useState(false);
  const [validandoCorreo, setValidandoCorreo] = useState(false);
  const [pasoActual, setPasoActual] = useState('inicio');
  const [reconocimientoActivo, setReconocimientoActivo] = useState(false);
  const [mensajeEnviado, setMensajeEnviado] = useState(false);
  const [repetirTimer, setRepetirTimer] = useState(null);
  const [cargando, setCargando] = useState(false);
  const navigate = useNavigate();
  const recognitionRef = useRef(null);

  useEffect(() => {
    document.body.classList.toggle('dark', modoOscuro);

    if ('webkitSpeechRecognition' in window) {
      const recognition = new window.webkitSpeechRecognition();
      recognition.lang = 'es-ES';
      recognition.continuous = false;
      recognition.interimResults = false;
      recognitionRef.current = recognition;

      recognition.onresult = manejarResultadoVoz;
      recognition.onerror = (event) => {
        console.error('Error en reconocimiento:', event.error);
        hablar('Hubo un error con el micrófono. Por favor intenta nuevamente.');
      };
    }

    iniciarPaso('inicio');

    return () => {
      if (repetirTimer) clearTimeout(repetirTimer);
      if (recognitionRef.current) recognitionRef.current.abort();
    };
  }, [modoOscuro]);

  useEffect(() => {
    const timer = setTimeout(() => {
      if (formData.correo) verificarCorreo(formData.correo, setCorreoExistente, setValidandoCorreo);
    }, 500);
    return () => clearTimeout(timer);
  }, [formData.correo]);

  const hablar = (texto, callback) => {
    if (cargando) return;

    setReconocimientoActivo(false);
    if (recognitionRef.current) recognitionRef.current.abort();
    if (repetirTimer) clearTimeout(repetirTimer);

    setCargando(true);
    const mensaje = new SpeechSynthesisUtterance(texto);
    mensaje.lang = 'es-ES';

    mensaje.onend = () => {
      setCargando(false);
      if (callback) callback();

      // Pequeño retraso antes de activar reconocimiento
      setTimeout(() => {
        setReconocimientoActivo(true);
        iniciarReconocimiento();
      }, 500);

      // Configurar repetición si no hay respuesta
      setRepetirTimer(setTimeout(() => {
        if (reconocimientoActivo) {
          hablar(texto, callback);
        }
      }, 10000));
    };

    window.speechSynthesis.speak(mensaje);
  };

  const iniciarPaso = (paso) => {
    setPasoActual(paso);

    switch (paso) {
      case 'inicio':
        hablar('Este es el formulario de registro para discapacitados visuales. Por favor, di tu nombre.');
        break;
      case 'confirmarNombre':
        hablar(`Dijiste que tu nombre es: ${datoTemporal}. ¿Es correcto? Di sí o no.`);
        break;
      case 'apellido':
        hablar('Ahora por favor, di tu apellido.');
        break;
      case 'confirmarApellido':
        hablar(`Dijiste que tu apellido es: ${datoTemporal}. ¿Es correcto? Di sí o no.`);
        break;
      case 'correo':
        hablar('Ahora indica tu correo electrónico. Di tu correo reemplazando el @ por la palabra "arroba".');
        break;
      case 'confirmarCorreo':
        const correoFormateado = datoTemporal.replace(' arroba ', '@').replace(/\s/g, '');
        hablar(`Dijiste que tu correo es: ${correoFormateado}. ¿Es correcto? Di sí o no.`);
        break;
      case 'password':
        hablar('Ahora indica tu contraseña. Debe tener al menos 6 caracteres.');
        break;
      case 'confirmarPassword':
        hablar('Por favor, repite tu contraseña para confirmar.');
        break;
      case 'validarPassword':
        if (formData.password === datoTemporal) {
          setFormData(prev => ({ ...prev, confirmPassword: datoTemporal }));
          hablar('Contraseña confirmada correctamente. Ahora selecciona tu tipo de discapacidad visual.');
          setPasoActual('discapacidad');
        } else {
          hablar('Las contraseñas no coinciden. Por favor, indica tu contraseña nuevamente.');
          setPasoActual('password');
        }
        break;
      case 'discapacidad':
        hablar('Por favor, di el tipo de discapacidad visual que tienes. Opciones: ceguera total, baja visión, visión borrosa o fotofobia.');
        break;
      case 'registrar':
        manejarEnvioFormulario();
        break;
      default:
        break;
    }
  };

  const manejarResultadoVoz = (e) => {
    if (!reconocimientoActivo || cargando) return;

    const resultado = e.results[0][0].transcript.trim().toLowerCase();
    const esSi = resultado === 'sí' || resultado === 'si';
    const esNo = resultado === 'no';

    console.log(`Paso: ${pasoActual}, Resultado: ${resultado}`);

    switch (pasoActual) {
      case 'inicio':
        setDatoTemporal(resultado);
        setPasoActual('confirmarNombre');
        break;

      case 'confirmarNombre':
        if (esSi) {
          setFormData(prev => ({ ...prev, nombre: datoTemporal }));
          setPasoActual('apellido');
        } else if (esNo) {
          setPasoActual('inicio');
        } else {
          hablar(`No entendí tu respuesta. ¿${datoTemporal} es tu nombre? Di sí o no.`);
        }
        break;

      case 'apellido':
        setDatoTemporal(resultado);
        setPasoActual('confirmarApellido');
        break;

      case 'confirmarApellido':
        if (esSi) {
          setFormData(prev => ({ ...prev, apellido: datoTemporal }));
          setPasoActual('correo');
        } else if (esNo) {
          setPasoActual('apellido');
        } else {
          hablar(`No entendí tu respuesta. ¿${datoTemporal} es tu apellido? Di sí o no.`);
        }
        break;

      case 'correo':
        setDatoTemporal(resultado);
        setPasoActual('confirmarCorreo');
        break;

      case 'confirmarCorreo':
        if (esSi) {
          const correo = datoTemporal.replace(' arroba ', '@').replace(/\s/g, '');
          setFormData(prev => ({ ...prev, correo }));
          setPasoActual('password');
        } else if (esNo) {
          setPasoActual('correo');
        } else {
          const correoFormateado = datoTemporal.replace(' arroba ', '@').replace(/\s/g, '');
          hablar(`No entendí tu respuesta. ¿${correoFormateado} es tu correo? Di sí o no.`);
        }
        break;

      case 'password':
        setFormData(prev => ({ ...prev, password: resultado }));
        setPasoActual('confirmarPassword');
        break;

      case 'confirmarPassword':
        setDatoTemporal(resultado);
        setPasoActual('validarPassword');
        break;

      case 'discapacidad':
        const discapacidadValida = [
          'ceguera total', 
          'baja visión', 
          'visión borrosa', 
          'fotofobia'
        ].includes(resultado);

        if (discapacidadValida) {
          const valorDiscapacidad = resultado.replace(' ', '');
          setFormData(prev => ({ ...prev, discapacidad: valorDiscapacidad }));
          hablar(`Has seleccionado ${resultado}. ¿Quieres agregar alguna descripción adicional? Di "no" si no es necesario.`);
          setPasoActual('descripcion');
        } else {
          hablar('No reconocí ese tipo de discapacidad. Por favor di: ceguera total, baja visión, visión borrosa o fotofobia.');
        }
        break;

      case 'descripcion':
        if (esNo) {
          setPasoActual('registrar');
        } else {
          setFormData(prev => ({ ...prev, descripcion: resultado }));
          setPasoActual('registrar');
        }
        break;

      default:
        break;
    }
  };

  const iniciarReconocimiento = () => {
    if (cargando) return;

    const recognition = recognitionRef.current;
    if (!recognition) return;

    try {
      recognition.stop();
      setTimeout(() => {
        recognition.start();
      }, 300);
    } catch (error) {
      console.error("Error al iniciar reconocimiento:", error);
      hablar("No pude activar el micrófono. Por favor intenta nuevamente.");
    }
  };

  const handleChange = e => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const manejarEnvioFormulario = async () => {
    if (correoExistente) {
      hablar('Este correo ya está registrado. Intenta con otro.', () => setPasoActual('correo'));
      return;
    }

    if (formData.password.length < 6) {
      hablar('La contraseña debe tener al menos 6 caracteres.', () => setPasoActual('password'));
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      hablar('Las contraseñas no coinciden.', () => setPasoActual('password'));
      return;
    }

    if (!formData.discapacidad) {
      hablar('Debes seleccionar un tipo de discapacidad.', () => setPasoActual('discapacidad'));
      return;
    }

    try {
      const res = await fetch('http://localhost:4000/api/RegistroDiscapacitados', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      if (res.ok) {
        setMensajeEnviado(true);
        hablar('¡Registro exitoso! Serás redirigido al inicio de sesión.', () => navigate('/login'));
      } else {
        const errorData = await res.json();
        hablar(`Error al registrar: ${errorData.message || 'Intenta más tarde'}`);
      }
    } catch (err) {
      console.error('Error:', err);
      hablar('Ocurrió un error al conectar con el servidor. Intenta más tarde.');
    }
  };

  return (
    <>
      <BotonesModoOscuroVolver modoOscuro={modoOscuro} setModoOscuro={setModoOscuro} />

      <div className="contenedor-microfono">
        <button
          aria-label="Activar micrófono para dictar información"
          aria-describedby="instruccion-mic"
          onClick={iniciarReconocimiento}
          disabled={cargando}
          className={`boton-microfono ${reconocimientoActivo ? 'activo' : ''}`}>
          <img
            src={reconocimientoActivo 
              ? "https://cdn-icons-png.flaticon.com/512/4712/4712035.png" 
              : "https://cdn-icons-png.flaticon.com/512/4712/4712109.png"}
            alt={reconocimientoActivo ? "Micrófono activo" : "Micrófono inactivo"}
            className="icono-microfono"
            aria-hidden="true"
          />
        </button>
        <span id="instruccion-mic" hidden>
          Presiona este botón para activar el micrófono. Luego, sigue las instrucciones habladas.
        </span>
        {cargando && <div className="cargando-voz">Procesando...</div>}
      </div>

      <div className="registro-container">
        <form onSubmit={e => e.preventDefault()}>
          <h2>Formulario de Registro</h2>

          <label htmlFor="nombre">Nombre</label>
          <input type="text" id="nombre" name="nombre" value={formData.nombre} onChange={handleChange} required />

          <label htmlFor="apellido">Apellido</label>
          <input type="text" id="apellido" name="apellido" value={formData.apellido} onChange={handleChange} required />

          <label htmlFor="correo">Correo electrónico: 
            {correoExistente && <span className="correo-registrado"> * Este correo ya está registrado</span>}
            {validandoCorreo && <span className="validando-correo"> (Validando...)</span>}
          </label>
          <input type="email" id="correo" name="correo" value={formData.correo} onChange={handleChange} required />

          <label htmlFor="password">Contraseña (mínimo 6 caracteres)</label>
          <input type="password" id="password" name="password" value={formData.password} onChange={handleChange} required />

          <label htmlFor="confirmPassword">Confirmar contraseña</label>
          <input type="password" id="confirmPassword" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} required />

          <label htmlFor="discapacidad">Tipo de discapacidad</label>
          <select id="discapacidad" name="discapacidad" value={formData.discapacidad} onChange={handleChange} required>
            <option value="">Seleccione un tipo de discapacidad visual</option>
            <option value="cegueraTotal">Ceguera total</option>
            <option value="bajaVision">Baja visión</option>
            <option value="visionBorrosa">Visión borrosa</option>
            <option value="fotofobia">Fotofobia</option>
          </select>

          <label htmlFor="descripcion">Descripción adicional (opcional)</label>
          <textarea id="descripcion" name="descripcion" value={formData.descripcion} onChange={handleChange} rows="4" />

          <div className="botones-envio">
            <button 
              type="button" 
              className="btn-enviar" 
              onClick={manejarEnvioFormulario}
              disabled={cargando}>
              {cargando ? 'Procesando...' : 'Registrarse'}
            </button>
            <div className="texto-cuenta">
              ¿Ya tienes una cuenta?{' '}
              <button 
                type="button" 
                className="btn-iniciar-sesion" 
                onClick={() => navigate('/login')}
                disabled={cargando}>
                Inicia sesión
              </button>
            </div>
          </div>

          {mensajeEnviado && <p className="mensaje-exito">¡Registro exitoso!</p>}
        </form>
      </div>
    </>
  );
}

export default FormularioDiscapacitados;
