// src/Home.jsx
import React from 'react';
import { Link } from 'react-router-dom';
import './styles/Home.css';

const Home = () => {
  return (
    <div className="home-container">
      <nav className="navbar">
        <div className="navbar-left">
          <img src="/bus-icon.png" alt="Logo" className="logo" />
          <span className="brand">NaviCali</span>
        </div>
        <div className="navbar-right">
          <button><Link to="/registro" className="nav-link">Registrarse</Link></button>
          <button className="login-button">Iniciar Sesión</button>
        </div>
      </nav>

      <main className="main-content">
        <h1>Bienvenido a la App de Navegación Accesible</h1>
        <p className="subtitle">Conecta personas con discapacidad visual, acompañantes y conductores para una movilidad más inclusiva en Cali.</p>
        
        <h2>¿Cómo funciona?</h2>
        <p className="description">
          Nuestra plataforma permite que las personas con discapacidad visual accedan a rutas de transporte público con ayuda de acompañantes o conductores registrados. Todo está diseñado para ser accesible, confiable y fácil de usar.
        </p>

        <Link to="/registro" className="main-button">
          Comienza tu registro
        </Link>
      </main>
    </div>
  );
};

export default Home;
