import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import BotonesModoOscuroVolver from '../components/BotonesModoOscuroVolver';
import { verificarCorreo } from '../utils/verificarCorreo';
import '../styles/light.css';
import '../styles/dark.css';

function FormularioDiscapacitados() {
  const [formData, setFormData] = useState({
    nombre: '',
    apellido: '',
    correo: '',
    password: '',
    confirmPassword: '',
    discapacidad: '',
    descripcion: ''
  });

  const [modoOscuro, setModoOscuro] = useState(false);
  const [correoExistente, setCorreoExistente] = useState(false);
  const [validandoCorreo, setValidandoCorreo] = useState(false);
  const [pasoActual, setPasoActual] = useState(0);
  const [reconocimientoActivo, setReconocimientoActivo] = useState(false);
  const [mensajeEnviado, setMensajeEnviado] = useState(false);
  const [cargando, setCargando] = useState(false);
  const recognitionRef = useRef(null);
  const navigate = useNavigate();

  const nombreRef = useRef(null);
  const apellidoRef = useRef(null);
  const correoRef = useRef(null);
  const passwordRef = useRef(null);
  const confirmPasswordRef = useRef(null);
  const discapacidadRef = useRef(null);
  const descripcionRef = useRef(null);

  const pasos = ['nombre', 'apellido', 'correo', 'password', 'discapacidad', 'descripcion', 'registrar'];

  const instrucciones = {
    nombre: 'Este es el formulario de registro para discapacitados visuales. Por favor, di tu nombre.',
    apellido: 'Ahora por favor, di tu apellido.',
    correo: 'Ahora indica tu correo electrónico. Di tu correo reemplazando el arroba por la palabra arroba.',
    password: 'Ahora indica tu contraseña. Debe tener al menos 6 caracteres.',
    discapacidad: 'Por favor, di el tipo de discapacidad visual que tienes. Opciones: ceguera total, baja visión, visión borrosa o fotofobia.',
    descripcion: '¿Deseas agregar una descripción adicional? Di no si no es necesario.',
    registrar: 'Registrando tu información.'
  };

  const referencias = {
    nombre: nombreRef,
    apellido: apellidoRef,
    correo: correoRef,
    password: passwordRef,
    discapacidad: discapacidadRef,
    descripcion: descripcionRef
  };

  // eslint-disable-next-line react-hooks/exhaustive-deps
  useEffect(() => {
    document.body.classList.toggle('dark', modoOscuro);

    if ('webkitSpeechRecognition' in window) {
      const recognition = new window.webkitSpeechRecognition();
      recognition.lang = 'es-ES';
      recognition.continuous = false;
      recognition.interimResults = false;

      recognition.onresult = (event) => {
        const resultado = event.results?.[0]?.[0]?.transcript.trim().toLowerCase();
        if (resultado) manejarResultado(resultado);
      };

      recognition.onerror = (event) => {
        hablar('Hubo un error con el micrófono. Por favor intenta nuevamente.');
        console.error('Reconocimiento error:', event.error);
      };

      recognitionRef.current = recognition;
    }

    setReconocimientoActivo(true);
    iniciarPaso(0);
  }, []);

  useEffect(() => {
    const timer = setTimeout(() => {
      if (formData.correo) {
        verificarCorreo(formData.correo, setCorreoExistente, setValidandoCorreo);
      }
    }, 500);
    return () => clearTimeout(timer);
  }, [formData.correo]);

  const hablar = (texto, callback = null, autoReconocer = true) => {
    setReconocimientoActivo(false);
    recognitionRef.current?.abort();

    const msg = new SpeechSynthesisUtterance(texto);
    msg.lang = 'es-ES';
    msg.onend = () => {
      if (callback) callback();
      if (autoReconocer) {
        setTimeout(() => iniciarReconocimiento(), 800);
      }
    };

    window.speechSynthesis.cancel();
    window.speechSynthesis.speak(msg);
  };

  const iniciarPaso = (index) => {
    setPasoActual(index);
    const paso = pasos[index];
    const ref = referencias[paso];
    if (ref?.current) ref.current.focus();

    setTimeout(() => {
      hablar(instrucciones[paso]);
    }, 400);
  };

  const avanzarPaso = () => {
    setPasoActual(prevPaso => {
      const siguiente = Math.min(prevPaso + 1, pasos.length - 1);
      setTimeout(() => iniciarPaso(siguiente), 200);
      return siguiente;
    });
  };

  const iniciarReconocimiento = () => {
    if (!recognitionRef.current) return;
    try {
      recognitionRef.current.abort();
      recognitionRef.current.start();
    } catch (error) {
      console.error('Error iniciando reconocimiento:', error);
      hablar('Hubo un error iniciando el micrófono.');
    }
  };

  const manejarResultado = (resultado) => {
    const paso = pasos[pasoActual];
    const mapDiscapacidad = {
      'ceguera total': 'cegueraTotal',
      'baja visión': 'bajaVision',
      'visión borrosa': 'visionBorrosa',
      'fotofobia': 'fotofobia'
    };

    switch (paso) {
      case 'nombre':
      case 'apellido':
        setFormData(prev => ({ ...prev, [paso]: resultado }));
        avanzarPaso();
        break;

      case 'correo':
        const correo = resultado.replace(' arroba ', '@').replace(/\s/g, '');
        setFormData(prev => ({ ...prev, correo }));
        avanzarPaso();
        break;

      case 'password':
        setFormData(prev => ({
          ...prev,
          password: resultado,
          confirmPassword: resultado
        }));
        avanzarPaso();
        break;

      case 'discapacidad':
        if (mapDiscapacidad[resultado]) {
          setFormData(prev => ({ ...prev, discapacidad: mapDiscapacidad[resultado] }));
          avanzarPaso();
        } else {
          hablar('Tipo no reconocido. Di: ceguera total, baja visión, visión borrosa o fotofobia.');
        }
        break;

      case 'descripcion':
        if (['no', 'nada', 'ninguna'].includes(resultado)) {
          avanzarPaso();
        } else {
          setFormData(prev => ({ ...prev, descripcion: resultado }));
          avanzarPaso();
        }
        break;

      default:
        break;
    }
  };

  const manejarEnvioFormulario = async () => {
    if (correoExistente) return hablar('Este correo ya está registrado.', () => iniciarPaso(2));
    if (formData.password.length < 6) return hablar('La contraseña debe tener al menos 6 caracteres.', () => iniciarPaso(3));
    if (formData.password !== formData.confirmPassword) return hablar('Las contraseñas no coinciden.', () => iniciarPaso(3));
    if (!formData.discapacidad) return hablar('Debes seleccionar un tipo de discapacidad.', () => iniciarPaso(4));

    try {
      setCargando(true);
      const res = await fetch('http://localhost:4000/api/RegistroDiscapacitados', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      if (res.ok) {
        setMensajeEnviado(true);
        hablar('¡Registro exitoso! Serás redirigido al inicio de sesión.', () => navigate('/login'), false);
      } else {
        const err = await res.json();
        hablar(`Error al registrar: ${err.message || 'Intenta más tarde'}`);
      }
    } catch (err) {
      console.error(err);
      hablar('Ocurrió un error al conectar con el servidor. Intenta más tarde.');
    } finally {
      setCargando(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  return (
    <>
      <BotonesModoOscuroVolver modoOscuro={modoOscuro} setModoOscuro={setModoOscuro} />

      <div className="contenedor-microfono">
        <button
          aria-label="Activar micrófono para dictar información"
          aria-describedby="instruccion-mic"
          onClick={iniciarReconocimiento}
          disabled={cargando}
          className={`boton-microfono ${reconocimientoActivo ? 'activo' : ''}`}>
          <img
            src={reconocimientoActivo
              ? "https://cdn-icons-png.flaticon.com/512/4712/4712035.png"
              : "https://cdn-icons-png.flaticon.com/512/4712/4712109.png"}
            alt={reconocimientoActivo ? "Micrófono activo" : "Micrófono inactivo"}
            className="icono-microfono"
            aria-hidden="true"
          />
        </button>
        <span id="instruccion-mic" hidden>
          Presiona este botón para activar el micrófono. Luego, sigue las instrucciones habladas.
        </span>
        {cargando && <div className="cargando-voz">Procesando...</div>}
      </div>

      <div className="registro-container">
        <form onSubmit={e => e.preventDefault()}>
          <h2>Formulario de Registro</h2>

          <label htmlFor="nombre">Nombre</label>
          <input ref={nombreRef} type="text" id="nombre" name="nombre" value={formData.nombre} onChange={handleChange} required />

          <label htmlFor="apellido">Apellido</label>
          <input ref={apellidoRef} type="text" id="apellido" name="apellido" value={formData.apellido} onChange={handleChange} required />

          <label htmlFor="correo">Correo electrónico:
            {correoExistente && <span className="correo-registrado"> * Este correo ya está registrado</span>}
            {validandoCorreo && <span className="validando-correo"> (Validando...)</span>}
          </label>
          <input ref={correoRef} type="email" id="correo" name="correo" value={formData.correo} onChange={handleChange} required />

          <label htmlFor="password">Contraseña (mínimo 6 caracteres)</label>
          <input ref={passwordRef} type="password" id="password" name="password" value={formData.password} onChange={handleChange} required />

          <label htmlFor="confirmPassword">Confirmar contraseña</label>
          <input ref={confirmPasswordRef} type="password" id="confirmPassword" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} required />

          <label htmlFor="discapacidad">Tipo de discapacidad</label>
          <select ref={discapacidadRef} id="discapacidad" name="discapacidad" value={formData.discapacidad} onChange={handleChange} required>
            <option value="">Seleccione un tipo de discapacidad visual</option>
            <option value="cegueraTotal">Ceguera total</option>
            <option value="bajaVision">Baja visión</option>
            <option value="visionBorrosa">Visión borrosa</option>
            <option value="fotofobia">Fotofobia</option>
          </select>

          <label htmlFor="descripcion">Descripción adicional (opcional)</label>
          <textarea ref={descripcionRef} id="descripcion" name="descripcion" value={formData.descripcion} onChange={handleChange} rows="4" />

          <div className="botones-envio">
            <button type="button" className="btn-enviar" onClick={manejarEnvioFormulario} disabled={cargando}>
              {cargando ? 'Procesando...' : 'Registrarse'}
            </button>
            <div className="texto-cuenta">
              ¿Ya tienes una cuenta?{' '}
              <button type="button" className="btn-iniciar-sesion" onClick={() => navigate('/login')} disabled={cargando}>
                Inicia sesión
              </button>
            </div>
          </div>

          {mensajeEnviado && <p className="mensaje-exito">¡Registro exitoso!</p>}
        </form>
      </div>
    </>
  );
}

export default FormularioDiscapacitados;
