import React from 'react';

function BotonesModoOscuroPerfil({ modoOscuro, setModoOscuro, bloquearEventos, reconocimientoActivo }) {
  // Función para hablar usando síntesis de voz
  const hablar = (texto) => {
    if (bloquearEventos || reconocimientoActivo) return; // Evita hablar si está bloqueado o escuchando
    const mensaje = new SpeechSynthesisUtterance(texto);
    mensaje.lang = "es-ES";
    speechSynthesis.cancel();
    speechSynthesis.speak(mensaje);
  };

  const manejarFocoVolver = () => {
    hablar("Botón para volver a tu perfil");
  };

  const manejarClickVolver = () => {
    if (bloquearEventos) return;
    hablar("Has regresado a la pagina de tu perfil, presiona el boton del centro para iniciar una nueva ruta");
    setTimeout(() => {
      window.location.href = "/Perfil";
    }, 500); // Da tiempo para que se escuche el mensaje antes de redirigir
  };

  return (
    <div className="botones-laterales">
        {/* Botón volver a la izquierda */}
        <button
            type="button"
            className="btn-volver-perfil"
            onFocus={manejarFocoVolver}
            onTouchStart={manejarFocoVolver}
            onMouseEnter={manejarFocoVolver}
            onClick={manejarClickVolver}
            aria-label="Volver al perfil"
        >
            ⬅ Volver
        </button>

        {/* Botón modo oscuro a la derecha */}
        <button
            type="button"
            className="btn-modo-oscuro"
            onClick={() => setModoOscuro(!modoOscuro)}
            aria-pressed={modoOscuro}
            aria-label={modoOscuro ? 'Cambiar a modo claro' : 'Cambiar a modo oscuro'}
        >
            {modoOscuro ? 'Modo Claro' : 'Modo Oscuro'}
        </button>
    </div>
  );
}

export default BotonesModoOscuroPerfil;
