import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './styles/Home.css';
import MapaCali from './MapaCali'; // ← Importamos el componente

const Home = () => {
  const [mostrarMapa, setMostrarMapa] = useState(false);

  return (
    <div className="home-container">
      <div className="navbar">
        <div className="navbar-left">
          <h1 className="app-title">NaviGuía</h1>
        </div>
        <div className="navbar-right">
          <Link to="/registro" className="nav-button">Registrarse</Link>
          <Link to="/login" className="nav-button login">Iniciar sesión</Link>
        </div>
      </div>

      <div className="home-content">
        <div className="welcome-box">
          <div className="welcome-text">
            <h2>Bienvenido a NaviGuía</h2>
            <p>Una herramienta para personas con discapacidad visual para moverse por Cali usando el transporte público.</p>
            <h3>¿Cómo funciona?</h3>
            <p>Registrándote como usuario, puedes ver rutas, horarios y recibir orientación sobre qué transporte tomar.</p>

            <button
              className="nav-button"
              style={{ marginTop: '20px' }}
              onClick={() => setMostrarMapa(!mostrarMapa)}
            >
              {mostrarMapa ? 'Ocultar Mapa de Cali' : 'Ver Mapa de Cali'}
            </button>

            {mostrarMapa && <MapaCali />}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
