// src/MapaCali.jsx
import React, { useRef, useEffect } from 'react';
import mapboxgl from 'mapbox-gl';
import './styles/MapaCali.css'; // crea este archivo despuÃ©s

// ðŸ”‘ Usa tu token de acceso de Mapbox (regÃ­strate en https://www.mapbox.com/ si no tienes uno)
mapboxgl.accessToken = 'pk.eyJ1IjoibnlhbnRhY2F0IiwiYSI6ImNtYzNvbGVjZDA3ZDEya29scHZoY25qaGUifQ.2bOVqwSZgFeQbN_End3gyQ';

const MapaCali = () => {
  const mapContainerRef = useRef(null);

  useEffect(() => {
    const map = new mapboxgl.Map({
      container: mapContainerRef.current,
      style: 'mapbox://styles/mapbox/streets-v12',
      center: [-76.5225, 3.4516], // Coordenadas de Cali
      zoom: 12,
    });

    return () => map.remove();
  }, []);

  return (
    <div className="map-container" ref={mapContainerRef}></div>
  );
};

export default MapaCali;
