// src/Home.jsx
import React from 'react';
import { Link } from 'react-router-dom';
import './styles/Home.css';

const Home = () => {
  return (
    <div className="home-container">
      <h1>Navega tu ciudad con confianza</h1>
      <p className="subtitle">
        Ayudamos a personas con discapacidad visual a movilizarse en el transporte público de Cali con seguridad y asistencia.
      </p>

      <div className="card-grid">
        <div className="card">
          <img src="/img/rutas.png" alt="Explora rutas" />
          <h2>Explora rutas</h2>
          <p>Consulta rutas seguras y accesibles del transporte público.</p>
          <Link to="/RegistroDiscapacitados" className="btn">Iniciar ahora</Link>
        </div>

        <div className="card">
          <img src="/img/acompanantes.png" alt="Acompañantes" />
          <h2>Registro de acompañantes</h2>
          <p>Los acompañantes pueden registrarse y asistir en los desplazamientos.</p>
          <Link to="/RegistroAcompanantes" className="btn">Registrarse</Link>
        </div>

        <div className="card">
          <img src="/img/conductores.png" alt="Conductores" />
          <h2>Registro de conductores</h2>
          <p>Conductores del sistema público pueden apoyar y recibir notificaciones.</p>
          <Link to="/RegistroConductores" className="btn">Ver más</Link>
        </div>
      </div>
    </div>
  );
};

export default Home;
