import '../styles/light.css';
import '../styles/dark.css';

function FormularioDiscapacitados() {
  const [formData, setFormData] = useState({
  nombre: '',
  apellido: '',
  correo: '',
  password: '',
  confirmPassword: '',
  discapacidad: '',
  descripcion: ''
});

