import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import './styles/inicio.css';

function InicioRegistro() {
  // Bloquea clic derecho sobre imágenes
  useEffect(() => {
    const handleContextMenu = (e) => {
      if (e.target.tagName === 'IMG') {
        e.preventDefault();
      }
    };
    document.addEventListener("contextmenu", handleContextMenu);
    return () => {
      document.removeEventListener("contextmenu", handleContextMenu);
    };
  }, []);

  // Objeto con todas las descripciones
  const cardData = {
    discapacitados: {
      title: "Discapacitados Visuales",
      img: "/NaviGuiaAppD.jpeg",
      alt: "Usuario Discapacitado",
      description:
      "¡Nuestra App está desarrollada hacia personas con discapacidad visual para que puedan tomar rutas mas accesibles, faciles y seguras!. Si eres un acompañante o alguien con experiencia en apoyo a personas con este tipo de discapacidad, tus sugerencias nos seran de gran ayuda para mejorar la calidad de vida de muchas personas. Contactanos a traves de nuestra aplicacion o nuestras redes.",
      link: "/RegistroDiscapacitados",
    },
    acompanantes: {
      title: "Acompañantes",
      img: "/NaviGuiaAppA.jpeg",
      alt: "Acompañante",
      description: "¿Te gustaría hacer la diferencia en la vida de alguien? Regístrate como acompañante y brinda apoyo a personas con discapacidad visual en sus trayectos diarios. Cuéntanos sobre tu experiencia, tu zona de cobertura y tu disponibilidad. Tu compromiso puede abrir caminos, generar confianza y construir una comunidad más inclusiva.",
      link: "/RegistroAcompanantes",
    },
    conductores: {
      title: "Conductores",
      img: "/NaviGuiaAppC.jpeg",
      alt: "Conductor",
      description: "¿Eres conductor y quieres ser prestar tus servicios para generar impacto social? Regístrate como conductor y ayuda a que personas con discapacidad visual lleguen a sus destinos de forma rapida y segura. Adjunta tu licencia, placa, tipo de vehículo y SOAT. La responsabilidad y empatía son nuestro lema, la clave para prestar un servicio de calidad y transporte más humano e inclusivo.",
      link: "/RegistroConductores",
    },
  };

  return (
    <div className="home-container" role="main" aria-labelledby="titulo-registro">
      <header className="navbar" role="banner">
        <div className="navbar-left">
          <span role="img" aria-label="Logo de NaviGuía">🧭</span>
          <h1 className="app-title" id="titulo-registro">NaviGuía - Registro de Usuarios</h1>
        </div>
        <div className="navbar-right">
          <Link to="/" className="nav-button" aria-label="Volver a la página principal">Volver al inicio</Link>
        </div>
      </header>
      <div className="cards-container" role="region" aria-label="Opciones de registro">
        {Object.values(cardData).map((card, index) => (
           <Link to={card.link} key={index} className="card-link" tabIndex={0} aria-label={`Ir al formulario de ${card.title}`}>
            <div className="card" role="group" aria-labelledby={`card-title-${index}`}> 
              <h2 id={`card-title-${index}`}>{card.title}</h2>
              <img src={card.img} alt={card.alt + '. Imagen ilustrativa.'} draggable="false" />
              <p>{card.description}</p>
              <span className="btn-card" aria-hidden="true">Ir al formulario</span>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default InicioRegistro;
