// FormularioAcompanante.js
import React, { useState, useEffect } from 'react';
import './styles/light.css';
import './styles/dark.css';

function FormularioAcompanantes() {
  const [formData, setFormData] = useState({
    nombre: "",
    apellido: "",
    correo: "",
    zona: "",
    experiencia: "",
    horarios: "",
    password: "", // Nuevo campo
    confirmPassword: "" // Campo para confirmar contraseña
  });
  const [modoOscuro, setModoOscuro] = useState(false);
  const [mensajeEnviado, setMensajeEnviado] = useState(false);
  const passwordRef = React.useRef(null);

  useEffect(() => {
    document.body.classList.toggle('dark', modoOscuro);
  }, [modoOscuro]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData({
      ...formData,
      [name]: files ? files[0] : value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.password !== formData.confirmPassword) {
      alert('Las contraseñas no coinciden.');
      if (passwordRef.current) passwordRef.current.focus();
      return;
    }
    try {
      const response = await fetch('http://localhost:4000/api/RegistroAcompanantes', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        setMensajeEnviado(true);
        setTimeout(() => setMensajeEnviado(false), 3000);
        setFormData({
          nombre: "", apellido: "", correo: "", zona: "",
          experiencia: "", horarios: "", password: "", confirmPassword: ""
        });
      } else {
        console.error("Error al registrar acompañante");
      }
    } catch (err) {
      console.error("Error en conexión:", err);
    }
  };

  return (
    <>
      {/* Botones volver al inicio y modo oscuro juntos, alineados y centrados */}
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '1.5rem', margin: '2.5rem auto 1.5rem auto', maxWidth: 520 }}>
        <button
          type="button"
          className="btn-primary"
          onClick={() => window.location.href = "/"}
          style={{
            fontWeight: 600,
            fontSize: '1.05rem',
            borderRadius: '6px',
            padding: '0.7rem 1.5rem',
            border: '2px solid #1976d2',
            background: '#1976d2',
            color: '#fff',
            cursor: 'pointer',
            maxWidth: 250,
            transition: 'background 0.2s, color 0.2s'
          }}
          aria-label="Volver al inicio"
        >
          Volver al inicio
        </button>
        <button
          type="button"
          className="btn-primary"
          onClick={() => setModoOscuro(!modoOscuro)}
          aria-pressed={modoOscuro}
          aria-label={modoOscuro ? 'Cambiar a modo claro' : 'Cambiar a modo oscuro'}
          style={{
            fontWeight: 600,
            fontSize: '1.05rem',
            borderRadius: '6px',
            padding: '0.7rem 1.5rem',
            border: '2px solid #1976d2',
            background: '#1976d2',
            color: '#fff',
            cursor: 'pointer',
            maxWidth: 250,
            transition: 'background 0.2s, color 0.2s',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            gap: '0.5rem'
          }}
        >
          {modoOscuro ? 'Modo claro' : 'Modo oscuro'}
        </button>
      </div>
      <div className="registro-container">
        <form onSubmit={handleSubmit}>
          <h2>Formulario de Registro</h2>

          <label>Nombre</label>
          <input type="text" name="nombre" value={formData.nombre} onChange={handleChange} required />

          <label>Apellido</label>
          <input type="text" name="apellido" value={formData.apellido} onChange={handleChange} required />

          <label>Correo</label>
          <input type="email" name="correo" value={formData.correo} onChange={handleChange} required />

          <label htmlFor="password">Contraseña</label>
          <input ref={passwordRef} type="password" id="password" name="password" value={formData.password} onChange={handleChange} required />

          <label htmlFor="confirmPassword">Confirmar contraseña</label>
          <input
            type="password"
            id="confirmPassword"
            name="confirmPassword"
            value={formData.confirmPassword || ''}
            onChange={handleChange}
            required
          />

          <label>Zona de disponibilidad</label>
          <input type="text" name="zona" value={formData.zona} onChange={handleChange} required />

          <label>¿Tiene experiencia en acompañamiento para personas con discapacidad visual?</label>
          <select name="experiencia" value={formData.experiencia} onChange={handleChange} required>
            <option value="">Seleccione una opción</option>
            <option value="si">Sí</option>
            <option value="no">No</option>
          </select>

          <label>Horarios disponibles</label>
          <input type="text" name="horarios" value={formData.horarios} onChange={handleChange} required />

          <div className="button-group" style={{ display: 'flex', flexDirection: 'column', gap: '15px', marginTop: '30px' }}>
            <button type="submit" className="btn-primary">
              Enviar
            </button>
            <button type="button" className="btn-secondary" onClick={() => window.location.href = "/login"}>
              Ya tengo cuenta - Iniciar sesión
            </button>
          </div>
          {mensajeEnviado && <p className="mensaje-exito">¡Has sido registrado correctamente!.</p>}
        </form>
      </div>
    </>
  );
}

export default FormularioAcompanantes;
