import React, { useState, useEffect, useRef } from 'react';
import './styles/light.css';
import './styles/dark.css';

function Login() {
  const [form, setForm] = useState({ correo: '', password: '' });
  const [modoOscuro, setModoOscuro] = useState(false);
  const [mensaje, setMensaje] = useState('');
  const mensajeRef = useRef(null);

  useEffect(() => {
    if (modoOscuro) {
      document.body.classList.add('dark');
    } else {
      document.body.classList.remove('dark');
    }
  }, [modoOscuro]);

  useEffect(() => {
    if (mensaje && mensajeRef.current) {
      mensajeRef.current.focus();
    }
  }, [mensaje]);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (!form.correo || !form.password) {
      setMensaje('Por favor, complete todos los campos.');
      return;
    }
    // Aquí iría la lógica real de autenticación
    setMensaje('¡Inicio de sesión exitoso!');
    setForm({ correo: '', password: '' });
  };

  const goToRegister = () => {
    window.location.href = "/RegistroDiscapacitados";
  };

  return (
    <>
      {/* Botones volver al inicio y modo oscuro juntos, alineados y centrados */}
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '1.5rem', margin: '2.5rem auto 1.5rem auto', maxWidth: 520 }}>
        <button
          type="button"
          className="btn-primary"
          onClick={() => window.location.href = "/"}
          style={{
            fontWeight: 600,
            fontSize: '1.05rem',
            borderRadius: '6px',
            padding: '0.7rem 1.5rem',
            border: '2px solid #1976d2',
            background: '#1976d2',
            color: '#fff',
            cursor: 'pointer',
            maxWidth: 250,
            transition: 'background 0.2s, color 0.2s'
          }}
          aria-label="Volver al inicio"
        >
          Volver al inicio
        </button>
        <button
          type="button"
          className="btn-primary"
          onClick={() => setModoOscuro(!modoOscuro)}
          aria-pressed={modoOscuro}
          aria-label={modoOscuro ? 'Cambiar a modo claro' : 'Cambiar a modo oscuro'}
          style={{
            fontWeight: 600,
            fontSize: '1.05rem',
            borderRadius: '6px',
            padding: '0.7rem 1.5rem',
            border: '2px solid #1976d2',
            background: '#1976d2',
            color: '#fff',
            cursor: 'pointer',
            maxWidth: 250,
            transition: 'background 0.2s, color 0.2s',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            gap: '0.5rem'
          }}
        >
          {modoOscuro ? 'Modo claro' : 'Modo oscuro'}
        </button>
      </div>

      <div className="registro-container">
        <form onSubmit={handleSubmit} aria-labelledby="titulo-login">
          <h2 id="titulo-login">Inicio de Sesión</h2>

          <div className="form-status" role="alert" aria-live="polite" style={{ display: mensaje ? 'block' : 'none' }}>
            {mensaje}
          </div>

          <div style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem', marginBottom: '2rem' }}>
            <div className="form-group" style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start' }}>
              <label htmlFor="correo" style={{ fontWeight: 600, marginBottom: '0.5rem' }}>Correo electrónico</label>
              <input
                type="email"
                id="correo"
                name="correo"
                value={form.correo}
                onChange={handleChange}
                required
                autoComplete="username"
                style={{ width: '95%', padding: '0.8rem', fontSize: '1.05rem', borderRadius: '6px', border: '1.5px solid #99ccff' }}
              />
            </div>

            <div className="form-group" style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start' }}>
              <label htmlFor="password" style={{ fontWeight: 600, marginBottom: '0.5rem' }}>Contraseña</label>
              <input
                type="password"
                id="password"
                name="password"
                value={form.password}
                onChange={handleChange}
                required
                autoComplete="current-password"
                style={{ width: '95%', padding: '0.8rem', fontSize: '1.05rem', borderRadius: '6px', border: '1.5px solid #99ccff' }}
              />
            </div>
          </div>

          <div className="button-group" style={{ display: 'flex', flexDirection: 'column', gap: '15px', marginTop: '30px' }}>
            <button type="submit" className="btn-primary">
              Ingresar
            </button>
            <button type="button" className="btn-secondary" onClick={goToRegister}>
              No tengo cuenta - Registrarse
            </button>
          </div>

          <div
            aria-live="polite"
            tabIndex={-1}
            ref={mensajeRef}
            style={{ outline: 'none', minHeight: '2.2rem', marginTop: '1rem' }}
          >
            {mensaje && (
              <p className={mensaje.includes('exitoso') ? 'mensaje-exito' : ''}>{mensaje}</p>
            )}
          </div>
        </form>
      </div>
    </>
  );
}

export default Login;
