import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import BotonesModoOscuroVolver from '../components/BotonesModoOscuroVolver';
import { verificarCorreo } from '../utils/verificarCorreo';
import '../styles/light.css';
import '../styles/dark.css';

function FormularioDiscapacitados() {
  const [formData, setFormData] = useState({
    nombre: '', apellido: '', correo: '',
    discapacidad: '', descripcion: '',
    password: '', confirmPassword: '',
    nombreTemp: '', apellidoTemp: '', correoTemp: '', passwordTemp: '',confirmPasswordTemp:""
  });

  const [modoOscuro, setModoOscuro] = useState(false);
  const [correoExistente, setCorreoExistente] = useState(false);
  const [validandoCorreo, setValidandoCorreo] = useState(false);
  const [pasoActual, setPasoActual] = useState('inicio');
  const [reconocimientoActivo, setReconocimientoActivo] = useState(false);
  const [mensajeEnviado, setMensajeEnviado] = useState(false);
  const navigate = useNavigate();
  const recognitionRef = useRef(null);
  const passwordRef = useRef(null);

  // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect(() => {
      document.body.classList.toggle('dark', modoOscuro);

      if ('webkitSpeechRecognition' in window) {
        const recognition = new window.webkitSpeechRecognition();
        recognition.lang = 'es-ES';
        recognition.continuous = false;
        recognition.interimResults = false;
        recognitionRef.current = recognition;
        recognition.onresult = manejarResultadoVoz;
      }

      iniciarPaso('inicio');
    }, []);

  useEffect(() => {
    const timer = setTimeout(() => {
      if (formData.correo) verificarCorreo(formData.correo, setCorreoExistente, setValidandoCorreo);
    }, 500);
    return () => clearTimeout(timer);
  }, [formData.correo]);

  const hablar = (texto, callback) => {
    setReconocimientoActivo(false); // 🔴 Pausar reconocimiento al hablar
    if (recognitionRef.current) {
      recognitionRef.current.abort(); // Detener si está activo
    }

    const mensaje = new SpeechSynthesisUtterance(texto);
    mensaje.lang = 'es-ES';
    mensaje.onend = () => {
      if (callback) callback();
      iniciarReconocimiento(); // 🔊 Activar reconocimiento después de hablar
    };
    window.speechSynthesis.speak(mensaje);
  };


  const iniciarPaso = paso => {
    setPasoActual(paso);
    setReconocimientoActivo(false);
    switch (paso) {
      case 'inicio':
        hablar('Este es el formulario de registro para discapacitados visuales. Presiona el micrófono para comenzar. Primero indícanos tu nombre.', () => setReconocimientoActivo(true));
        break;
      case 'confirmarNombre':
        hablar(`Tu nombre es: ${formData.nombreTemp}. ¿Es correcto? Di sí o no.`, () => setReconocimientoActivo(true));
        break;
      case 'apellido':
        hablar('Ahora indica tu apellido.', () => setReconocimientoActivo(true));
        break;
      case 'confirmarApellido':
        hablar(`Tu apellido es: ${formData.apellidoTemp}. ¿Es correcto? Di sí o no.`, () => setReconocimientoActivo(true));
        break;
      case 'correo':
        hablar('Ahora indica tu correo electrónico.', () => setReconocimientoActivo(true));
        break;
      case 'confirmarCorreo':
        hablar(`Tu correo es: ${formData.correoTemp}. ¿Es correcto? Di sí o no.`, () => setReconocimientoActivo(true));
        break;
      case 'password':
        hablar('Ahora indica tu contraseña. Solo se registrará si es segura.', () => setReconocimientoActivo(true));
        break;
      case 'confirmarPassword':
        hablar('¿Confirmas esta contraseña? Di sí o no.', () => setReconocimientoActivo(true));
        break;
      case 'confirmpassword':
        hablar('Ahora indica la repetición de tu contraseña. Solo se registrará si es igual a la contraseña ingresada anterior.', () => setReconocimientoActivo(true));
        break;
      case 'confirmarPasswordTemp':
        hablar('¿Confirmas la repetición de tu contraseña? Di sí o no.', () => setReconocimientoActivo(true));
        break;
      case 'registrar':
        manejarEnvioFormulario();
        break;
      default:
        break;
    }
  };

  const manejarResultadoVoz = (e) => {
    const resultado = e.results[0][0].transcript.trim().toLowerCase();
    if (!reconocimientoActivo) return;

    const esSi = resultado === 'sí' || resultado === 'si';
    const esNo = resultado === 'no';

    switch (pasoActual) {
      case 'inicio':
        setFormData(prev => ({ ...prev, nombreTemp: resultado }));
        iniciarPaso('confirmarNombre');
        break;

      case 'confirmarNombre':
        if (esSi) {
          setFormData(prev => ({ ...prev, nombre: prev.nombreTemp }));
          iniciarPaso('apellido');
        } else if (esNo) {
          iniciarPaso('inicio');
        } else {
          hablar(`Tu nombre es: ${formData.nombreTemp}. ¿Es correcto? Di sí o no.`, () => setReconocimientoActivo(true));
        }
        break;

      case 'apellido':
        setFormData(prev => ({ ...prev, apellidoTemp: resultado }));
        iniciarPaso('confirmarApellido');
        break;

      case 'confirmarApellido':
        if (esSi) {
          setFormData(prev => ({ ...prev, apellido: prev.apellidoTemp }));
          iniciarPaso('correo');
        } else if (esNo) {
          iniciarPaso('apellido');
        } else {
          hablar(`Tu apellido es: ${formData.apellidoTemp}. ¿Es correcto? Di sí o no.`, () => setReconocimientoActivo(true));
        }
        break;

      case 'correo':
        const correoLimpiado = resultado.replace(' arroba ', '@').replace(/\s/g, '');
        setFormData(prev => ({ ...prev, correoTemp: correoLimpiado }));
        iniciarPaso('confirmarCorreo');
        break;

      case 'confirmarCorreo':
        if (esSi) {
          setFormData(prev => ({ ...prev, correo: prev.correoTemp }));
          iniciarPaso('password');
        } else if (esNo) {
          iniciarPaso('correo');
        } else {
          hablar(`Tu correo es: ${formData.correoTemp}. ¿Es correcto? Di sí o no.`, () => setReconocimientoActivo(true));
        }
        break;

      case 'password':
        setFormData(prev => ({ ...prev, passwordTemp: resultado }));
        iniciarPaso('confirmarPassword');
        break;

      case 'confirmarPassword':
        if (esSi) {
          setFormData(prev => ({
            ...prev,
            password: prev.passwordTemp,
            confirmPassword: prev.passwordTemp
          }));
          iniciarPaso('registrar');
        } else if (esNo) {
          iniciarPaso('password');
        } else {
          hablar('¿Confirmas esta contraseña? Di sí o no.', () => setReconocimientoActivo(true));
        }
        break;

      default:
        break;

      case 'confirmPassword':
        if (esSi) {
          iniciarPaso('passwordTemp');
        } else if (esNo) {
          iniciarPaso('password');
        } else {
          hablar('¿Confirmas esta contraseña? Di sí o no.', () => setReconocimientoActivo(true));
        }
        break;

      case 'passwordTemp':
        setFormData(prev => ({ ...prev, confirmPasswordTemp: resultado }));
        iniciarPaso('confirmarPasswordTemp');
        break;

      case 'confirmarPasswordTemp':
        if (esSi) {
          // Validación final: contraseñas deben coincidir
          if (formData.passwordTemp === formData.confirmPasswordTemp) {
            setFormData(prev => ({
              ...prev,
              password: prev.passwordTemp,
              confirmPassword: prev.confirmPasswordTemp
            }));
            iniciarPaso('registrar');
          } else {
            hablar('Las contraseñas no coinciden. Vamos a intentarlo de nuevo.', () => iniciarPaso('password'));
          }
        } else if (esNo) {
          iniciarPaso('passwordTemp');
        } else {
          hablar('¿Confirmas la repetición de tu contraseña? Di sí o no.', () => setReconocimientoActivo(true));
        }
        break;
          }
  };

  const iniciarReconocimiento = () => {
    if (recognitionRef.current) recognitionRef.current.start();
  };

  const handleChange = e => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const manejarEnvioFormulario = async () => {
    if (correoExistente) return hablar('Este correo ya está registrado. Intenta con otro.', null);
    try {
      const res = await fetch('http://localhost:4000/api/RegistroDiscapacitados', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      if (res.ok) {
        setMensajeEnviado(true);
        hablar('¡Registro exitoso! Te diriges a inicio de sesión.', () => navigate('/login'));
      } else {
        hablar('Error al registrar. Intenta más tarde.', null);
      }
    } catch (err) {
      console.error('Error:', err);
      hablar('Ocurrió un error al registrar.', null);
    }
  };

  return (
    <>
      <BotonesModoOscuroVolver modoOscuro={modoOscuro} setModoOscuro={setModoOscuro} />

      <div className="contenedor-microfono">
        <button
          aria-label="Activar micrófono para dictar información"
          aria-describedby="instruccion-mic"
          onClick={iniciarReconocimiento}
          className="boton-microfono">
          <img
            src="https://cdn-icons-png.flaticon.com/512/4712/4712109.png"
            alt="Ícono de micrófono"
            className="icono-microfono"
            aria-hidden="true"
          />
        </button>
        <span id="instruccion-mic" hidden>
          Presiona este botón para activar el micrófono. Luego, sigue las instrucciones habladas.
        </span>
      </div>

      <div className="registro-container">
        <form onSubmit={e => e.preventDefault()}>
          <h2>Formulario de Registro</h2>

          <label htmlFor="nombre">Nombre</label>
          <input type="text" id="nombre" name="nombre" value={formData.nombre} onChange={handleChange} required />

          <label htmlFor="apellido">Apellido</label>
          <input type="text" id="apellido" name="apellido" value={formData.apellido} onChange={handleChange} required />

          <label htmlFor="correo">Correo electrónico: {correoExistente && (
            <span className ="correo-registrado">
              * Este correo ya está registrado
              </span>)}{validandoCorreo && (<span>
            </span>
          )}</label>
          <input type="email" id="correo" name="correo" value={formData.correo} onChange={handleChange} required />

          <label htmlFor="password">Contraseña</label>
          <input ref={passwordRef} type="password" id="password" name="password" value={formData.password} onChange={handleChange} required />

          <label htmlFor="confirmPassword">Confirmar contraseña</label>
          <input type="password" id="confirmPassword" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} required />

          <label htmlFor="discapacidad">Tipo de discapacidad</label>
          <select id="discapacidad" name="discapacidad" value={formData.discapacidad} onChange={handleChange} required>
            <option value="">Seleccione un tipo de discapacidad visual</option>
            <option value="cegueraTotal">Ceguera total</option>
            <option value="bajaVision">Baja visión</option>
            <option value="visionBorrosa">Visión borrosa</option>
            <option value="fotofobia">Fotofobia</option>
          </select>

          <label htmlFor="descripcion">Descripción adicional (opcional)</label>
          <textarea id="descripcion" name="descripcion" value={formData.descripcion} onChange={handleChange} rows="4" />

          <div className="botones-envio">
            <button type="button" className="btn-enviar" onClick={manejarEnvioFormulario}>Registrarse</button>
            <div className="texto-cuenta">
              ¿Ya tienes una cuenta?{' '}
              <button type="button" className="btn-iniciar-sesion" onClick={() => window.location.href = "/login"}>
                Inicia sesión
              </button>
            </div>
          </div>

          {mensajeEnviado && <p className="mensaje-exito">¡Has sido registrado correctamente!</p>}
        </form>
      </div>
    </>
  );
}

export default FormularioDiscapacitados;
